(29, 'App\\Models\\Admin', 2, 'created', 'App\\Models\\Tour', 9, '[]', '{\"tour_code\":\"jUO28zfaiG\",\"count_people\":\"20\",\"tour_name\":\"Tour \\u0110\\u00e0 N\\u1eb5ng 4N3D: S\\u01a1n Tr\\u00e0 - H\\u1ed9i An - B\\u00e0 N\\u00e0 (C\\u1ea7u V\\u00e0ng) - Hu\\u1ebf\",\"departure_location\":\"S\\u01a1n Tr\\u00e0\",\"destination\":\"H\\u1ed9i An - B\\u00e0 N\\u00e0 (C\\u1ea7u V\\u00e0ng) - Hu\\u1ebf\",\"province_id\":\"10\",\"price_adult\":\"2550000\",\"price_kid\":\"1550000\",\"single_room_price\":\"1000000\",\"tour_detail\":\"<h3>Chinh ph\\u1ee5c cung \\u0111\\u01b0\\u1eddng di s\\u1ea3n mi\\u1ec1n Trung<\\/h3>\\r\\n\\r\\n<p>Mi\\u1ec1n Trung, d\\u1ea3i \\u0111\\u1ea5t th&acirc;n th\\u01b0\\u01a1ng n\\u1ed1i li\\u1ec1n hai mi\\u1ec1n Nam - B\\u1eafc c\\u1ee7a Vi\\u1ec7t Nam. &ldquo;Cung \\u0111\\u01b0\\u1eddng di s\\u1ea3n mi\\u1ec1n Trung&rdquo; s\\u1ebd k\\u1ebft n\\u1ed1i du kh&aacute;ch v\\u1edbi c&aacute;c di s\\u1ea3n v\\u0103n h&oacute;a c\\u1ee7a nh&acirc;n lo\\u1ea1i \\u0111\\u01b0\\u1ee3c UNESCO c&ocirc;ng nh\\u1eadn bao g\\u1ed3m: Hu\\u1ebf v\\u1edbi hai di s\\u1ea3n l&agrave; Qu\\u1ea7n th\\u1ec3 di t&iacute;ch C\\u1ed1 \\u0111&ocirc; Hu\\u1ebf v&agrave; Nh&atilde; Nh\\u1ea1c cung \\u0111&igrave;nh; Qu\\u1ea3ng Nam v\\u1edbi hai di s\\u1ea3n l&agrave; Th&aacute;nh \\u0111\\u1ecba M\\u1ef9 S\\u01a1n v&agrave; \\u0111&ocirc; th\\u1ecb c\\u1ed5 H\\u1ed9i An. Tr&ecirc;n m\\u1ed7i b\\u01b0\\u1edbc \\u0111i, du kh&aacute;ch c&ograve;n c&oacute; th\\u1ec3 kh&aacute;m ph&aacute; cho m&igrave;nh nhi\\u1ec1u \\u0111i\\u1ec3m \\u0111\\u1ebfn h\\u1ea5p d\\u1eabn: B&agrave; N&agrave; Hill, L&agrave;ng Ch&agrave;i L\\u0103ng C&ocirc;, v&agrave; nhi\\u1ec1u b&atilde;i t\\u1eafm tuy\\u1ec7t \\u0111\\u1eb9p: C\\u1eeda \\u0110\\u1ea1i, Non N\\u01b0\\u1edbc, L\\u0103ng C&ocirc;, Nh\\u1eadt L\\u1ec7, c&ugrave;ng v\\u1edbi nh\\u1eefng n&eacute;t \\u1ea9m th\\u1ef1c \\u0111\\u1ed9c \\u0111&aacute;o c\\u1ee7a Mi\\u1ec1n Trung. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; cung \\