(30, 'App\\Models\\Admin', 2, 'created', 'App\\Models\\Tour', 10, '[]', '{\"tour_code\":\"cTYbXOuovU\",\"count_people\":\"25\",\"tour_name\":\"Tour \\u0110\\u00e0 N\\u1eb5ng 4N3D: H\\u1ed9i An - B\\u00e0 N\\u00e0 - Hu\\u1ebf\",\"departure_location\":\"H\\u1ed9i An\",\"destination\":\"Hu\\u1ebf\",\"province_id\":\"10\",\"price_adult\":\"3050000\",\"price_kid\":\"1050000\",\"single_room_price\":\"1000000\",\"tour_detail\":\"<h3>Chinh ph\\u1ee5c cung \\u0111\\u01b0\\u1eddng di s\\u1ea3n mi\\u1ec1n Trung<\\/h3>\\r\\n\\r\\n<p>Mi\\u1ec1n Trung, d\\u1ea3i \\u0111\\u1ea5t th&acirc;n th\\u01b0\\u01a1ng n\\u1ed1i li\\u1ec1n hai mi\\u1ec1n Nam - B\\u1eafc c\\u1ee7a Vi\\u1ec7t Nam. &ldquo;Cung \\u0111\\u01b0\\u1eddng di s\\u1ea3n mi\\u1ec1n Trung&rdquo; s\\u1ebd k\\u1ebft n\\u1ed1i du kh&aacute;ch v\\u1edbi c&aacute;c di s\\u1ea3n v\\u0103n h&oacute;a c\\u1ee7a nh&acirc;n lo\\u1ea1i \\u0111\\u01b0\\u1ee3c UNESCO c&ocirc;ng nh\\u1eadn bao g\\u1ed3m: Qu\\u1ea3ng B&igrave;nh c&ugrave;ng V\\u01b0\\u1eddn qu\\u1ed1c gia Phong Nha - K\\u1ebb B&agrave;ng; Hu\\u1ebf v\\u1edbi hai di s\\u1ea3n l&agrave; Qu\\u1ea7n th\\u1ec3 di t&iacute;ch C\\u1ed1 \\u0111&ocirc; Hu\\u1ebf v&agrave; Nh&atilde; Nh\\u1ea1c cung \\u0111&igrave;nh; Qu\\u1ea3ng Nam v\\u1edbi hai di s\\u1ea3n l&agrave; Th&aacute;nh \\u0111\\u1ecba M\\u1ef9 S\\u01a1n v&agrave; \\u0111&ocirc; th\\u1ecb c\\u1ed5 H\\u1ed9i An. Tr&ecirc;n m\\u1ed7i b\\u01b0\\u1edbc \\u0111i, du kh&aacute;ch c&ograve;n c&oacute; th\\u1ec3 kh&aacute;m ph&aacute; cho m&igrave;nh nhi\\u1ec1u \\u0111i\\u1ec3m \\u0111\\u1ebfn h\\u1ea5p d\\u1eabn: B&agrave; N&agrave; Hill, L&agrave;ng Ch&agrave;i L\\u0103ng C&ocirc;, v&agrave; nhi\\u1ec1u b&atilde;i t\\u1eafm tuy\\u1ec7t \\u0111\\u1eb9p: C\\u1eeda \\u0110\\u1ea1i, Non N\\u01b0\\u1edbc, L\\u0103ng C&ocirc;, Nh\\u1eadt L\\u1ec7, c&ugrave;ng v\\u1edbi nh\\u1eefng n&eacute;t \\u1ea9m th\\u1ef1c \\u0111\\u1ed9c \\u0111&aacute;o c\\u1ee7a Mi\\u1ec1n Trung. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; cung \\u0111\\u01b0\\u1eddng tuy\\u1ec7t v\\u1eddi n&agrave;y ngay h&ocirc;m nay!<br \\/>\\r\\n<em><strong>\\u0110\\u1eb7t \\u0111o&agrave;n ri&ecirc;ng li&ecirc;n h\\u1ec7 028 3933 8002 ho\\u1eb7c hotline 0906 355 542 \\u0111\\u1ec3 c&oacute; gi&aacute; t\\u1ed1t \\ud83d\\ude0d<\\/strong><\\/em><\\/p>\\r\\n\\r\\n<h3>Nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m th&uacute; v\\u1ecb trong ch\\u01b0\\u01a1ng tr&igrave;nh<\\/h3>\\r\\n\\r\\n<p>Du kh&aacute;ch s\\u1ebd kh&ocirc;ng ng\\u1eebng th\\u1ed5n th\\u1ee9c b\\u1edfi...<\\/p>\\r\\n\\r\\n<p>-&nbsp;<strong>H\\u1ed9i An<\\/strong>&nbsp;v\\u1edbi&nbsp;<strong>Ch&ugrave;a C\\u1ea7u Nh\\u1eadt B\\u1ea3n,&nbsp;Nh&agrave; C\\u1ed5&nbsp;h&agrave;ng tr\\u0103m n\\u0103m tu\\u1ed5i,&nbsp;H\\u1ed9i Qu&aacute;n Ph\\u01b0\\u1edbc Ki\\u1ebfn<\\/strong>&nbsp;&amp; X\\u01b0\\u1edfng th\\u1ee7 c&ocirc;ng m\\u1ef9 ngh\\u1ec7.<\\/p>\\r\\n\\r\\n<p>- L\\u01b0u l\\u1ea1i nh\\u1eefng b\\u1ee9c \\u1ea3nh \\u0111\\u1ec3 \\u0111\\u1eddi \\u1edf&nbsp;<strong>c\\u1ea7u B&agrave;n Tay V&agrave;ng<\\/strong>&nbsp;tr&ecirc;n&nbsp;<strong>B&agrave; N&agrave; Hills<\\/strong><\\/p>\\r\\n\\r\\n<p>- Th\\u01b0\\u1edfng th\\u1ee9c&nbsp;<strong>Ca Hu\\u1ebf tr&ecirc;n S&ocirc;ng H\\u01b0\\u01a1ng<\\/strong><\\/p>\\r\\n\\r\\n<p>-&nbsp;<strong>\\u0110\\u1ed9ng Thi&ecirc;n \\u0110\\u01b0\\u1eddng:<\\/strong>&nbsp;Ho&agrave;ng cung l&ograve;ng \\u0111\\u1ea5t s\\u1edf h\\u1eefu k\\u1ef3 quan tr&aacute;ng l\\u1ec7 v&agrave; huy\\u1ec1n \\u1ea3o b\\u1eadc nh\\u1ea5t th\\u1ebf gi\\u1edbi.&nbsp;<\\/p>\\r\\n\\r\\n<p>- Kh&aacute;m ph&aacute;&nbsp;<strong>\\u0110\\u1ea1i N\\u1ed9i Hu\\u1ebf<\\/strong>&nbsp;v\\u1edbi nh\\u1eefng ki\\u1ebfn tr&uacute;c \\u0111\\u1ed9c \\u0111&aacute;o c&ograve;n l\\u1ea1i th\\u1eddi nh&agrave; Nguy\\u1ec5n.<\\/p>\",\"tour_program\":\"<h3>Ch\\u01b0\\u01a1ng tr&igrave;nh tour<\\/h3>\\r\\n\\r\\n<h3>KHUY\\u1ebeN M&Atilde;I &Aacute;P D\\u1ee4NG T\\u1eea&nbsp;16\\/05\\/2020 &ndash; 15\\/07\\/2020.<\\/h3>\\r\\n\\r\\n<p>- T\\u1eb7ng v&eacute; Du thuy\\u1ec1n S&ocirc;ng H&agrave;n v\\u1edbi c&aacute;c d\\u1ecbch v\\u1ee5 ca m&uacute;a nh\\u1ea1c h\\u1ea5p d\\u1eabn.<\\/p>\\r\\n\\r\\n<p>- T\\u1eb7ng v&eacute; Buffet tr\\u01b0a t\\u1ea1i nh&agrave; h&agrave;ng 4 sao Banahills v\\u1edbi menu \\u1ea9m th\\u1ef1c 3 mi\\u1ec1n h\\u1ea5p d\\u1eabn v&agrave; phong ph&uacute;.<\\/p>\\r\\n\\r\\n<h3>NG&Agrave;Y 1: B\\u1ea2O T&Agrave;NG PH\\u1eacT H\\u1eccC \\u0110&Agrave; N\\u1eb4NG &ndash; H\\u1ed8I AN (\\u0102N TR\\u01afA, T\\u1ed0I)&nbsp;<\\/h3>\\r\\n\\r\\n<p><strong>S&aacute;ng:<\\/strong>&nbsp;\\u0110&oacute;n kh&aacute;ch t\\u1ea1i \\u0110&agrave; N\\u1eb5ng t\\u1eeb 7h00 &ndash; 09h00 (s&acirc;n bay\\/ga \\/\\u0111i\\u1ec3m h\\u1eb9n). V&ograve;ng qua&nbsp;<strong>C\\u1ea7u R\\u1ed3ng<\\/strong>&nbsp;l\\u01b0u l\\u1ea1i d\\u1ea5u \\u1ea5n tr&ecirc;n C\\u1ea7u T&igrave;nh Y&ecirc;u, t\\u1ea3n b\\u1ed9 th\\u01b0\\u1edfng th\\u1ee9c kh&ocirc;ng kh&iacute; trong l&agrave;nh b&ecirc;n&nbsp; b\\u1edd &nbsp;H&agrave;n Giang v\\u1edbi t\\u01b0\\u1ee3ng&nbsp;<strong>C&aacute; Ch&eacute;p H&oacute;a Long<\\/strong>&nbsp;- Bi\\u1ec3u t\\u01b0\\u1ee3ng mong mu\\u1ed1n v\\u01b0\\u01a1n l&ecirc;n c\\u1ee7a ng\\u01b0\\u1eddi \\u0110&agrave; N\\u1eb5ng. Tham quan&nbsp;<strong>Ch&ugrave;a Quan Th\\u1ebf &Acirc;m - &nbsp;B\\u1ea3o T&agrave;ng Ph\\u1eadt H\\u1ecdc v&agrave; L&agrave;ng Ngh\\u1ec1 \\u0110i&ecirc;u Kh\\u1eafc \\u0110&aacute;<\\/strong>. Mua s\\u1eafm \\u0111\\u1eb7c s\\u1ea3n Q&ugrave;a Mi\\u1ec1n Trung.<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"https:\\/\\/cdn2.ivivu.com\\/2018\\/08\\/23\\/15\\/ivivu-cau-tinh-yeu.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<p><em>C\\u1ea7u T&igrave;nh Y&ecirc;u \\u0110&agrave; N\\u1eb5ng.<\\/em><\\/p>\\r\\n\\r\\n<p><strong>Tr\\u01b0a:<\\/strong>&nbsp;\\u0102n tr\\u01b0a nh&agrave; h&agrave;ng.<\\/p>\\r\\n\\r\\n<p><strong>Chi\\u1ec1u:<\\/strong>&nbsp;Kh\\u1edfi h&agrave;nh v&agrave;o H\\u1ed9i An b&aacute;ch b\\u1ed9 tham quan v&agrave; mua s\\u1eafm&nbsp;<strong>Ph\\u1ed1 C\\u1ed5<\\/strong>&nbsp;v\\u1edbi:&nbsp;<strong>Ch&ugrave;a C\\u1ea7u Nh\\u1eadt B\\u1ea3n<\\/strong>,<strong>&nbsp;Nh&agrave; C\\u1ed5<\\/strong>&nbsp;h&agrave;ng tr\\u0103m n\\u0103m tu\\u1ed5i,&nbsp;<strong>H\\u1ed9i Qu&aacute;n Ph\\u01b0\\u1edbc Ki\\u1ebfn &amp; X\\u01b0\\u1edfng th\\u1ee7 c&ocirc;ng m\\u1ef9 ngh\\u1ec7<\\/strong>. Kh\\u1edfi h&agrave;nh v\\u1ec1 l\\u1ea1i \\u0110&agrave; N\\u1eb5ng<\\/p>\\r\\n\\r\\n<p><strong>T\\u1ed1i:<\\/strong>&nbsp;\\u0102n t\\u1ed1i nh&agrave; h&agrave;ng. T\\u1ef1 do th\\u01b0\\u1edfng ngo\\u1ea1n du thuy\\u1ec1n tr&ecirc;n&nbsp;<strong>s&ocirc;ng H&agrave;n<\\/strong>&nbsp;ng\\u1eafm c\\u1ea3nh&nbsp;<strong>\\u0110&agrave; Th&agrave;nh<\\/strong>&nbsp;v\\u1ec1 \\u0111&ecirc;m, ch\\u1ee5p \\u1ea3nh&nbsp;<strong>C\\u1ea7u Quay S&ocirc;ng H&agrave;n<\\/strong>, c\\u1ea7u R\\u1ed3ng phun l\\u1eeda v&agrave; n\\u01b0\\u1edbc v&agrave;o cu\\u1ed1i tu\\u1ea7n.<\\/p>\\r\\n\\r\\n<h3>NG&Agrave;Y 2: B&Agrave; N&Agrave; (C\\u1ea6U V&Agrave;NG) - C\\u1ed0 \\u0110&Ocirc; HU\\u1ebe (\\u0102N S&Aacute;NG, TR\\u01afA, T\\u1ed0I) &nbsp; &nbsp; &nbsp;&nbsp;<\\/h3>\\r\\n\\r\\n<p><strong>S&aacute;ng:<\\/strong>&nbsp;\\u0110i\\u1ec3m t&acirc;m. Kh\\u1edfi h&agrave;nh \\u0111i&nbsp;<strong>B&agrave; N&agrave; - N&uacute;i Ch&uacute;a<\\/strong>. Qu&yacute; kh&aacute;ch s\\u1ebd t\\u1eadn h\\u01b0\\u1edfng c\\u1ea3m gi&aacute;c b\\u1ed3ng b\\u1ec1n khi ng\\u1ed3i tr&ecirc;n Cabin l\\u01a1 l\\u1eefng gi\\u1eefa ch\\u1eebng m&acirc;y. Vi\\u1ebfng&nbsp;<strong>ch&ugrave;a Linh \\u1ee8ng<\\/strong>&nbsp;v\\u1edbi t\\u01b0\\u1ee3ng Ph\\u1eadt Th&iacute;ch Ca cao 27m, vi\\u1ebfng \\u0111\\u1ec1n th\\u1edd&nbsp;<strong>B&agrave; Ch&uacute;a M\\u1eabu Th\\u01b0\\u1ee3ng Ng&agrave;n<\\/strong>. Tham quan&nbsp;<strong>C\\u1ea7u v&agrave;ng - HOT<\\/strong>&nbsp;C&acirc;y c\\u1ea7u \\u0111\\u1ed9c \\u0111&aacute;o n\\u1eb1m trong v\\u01b0\\u1eddn Thi&ecirc;n Thai \\u1edf&nbsp;<strong>B&agrave; N&agrave; Hill<\\/strong>&nbsp;- \\u0111\\u01b0\\u1ee3c x&acirc;y d\\u1ef1ng tr&ecirc;n \\u0111\\u1ed9 cao 1.400m so v\\u1edbi m\\u1eb7t n\\u01b0\\u1edbc bi\\u1ec3n, c&acirc;y c\\u1ea7u g\\u1ed3m 8 nh\\u1ecbp v&agrave; c&oacute; \\u0111\\u1ed9 d&agrave;i l&agrave; 150m. C&acirc;y c\\u1ea7u \\u0111\\u01b0\\u1ee3c n&acirc;ng \\u0111\\u1ee1 b\\u1edfi \\u0111&ocirc;i b&agrave;n tay kh\\u1ed5ng l\\u1ed3. Ng\\u01b0\\u1eddi \\u0111i nh\\u01b0 l\\u1ea1c gi\\u1eefa ti&ecirc;n c\\u1ea3nh n\\u01a1i c&oacute; m&acirc;y, tr\\u1eddi bao la. (V&eacute; c&aacute;p treo B&agrave; N&agrave; t\\u1ef1 t&uacute;c).<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"https:\\/\\/cdn2.ivivu.com\\/2018\\/12\\/06\\/17\\/ivivu-cau-vang-ba-na.jpg\\\" style=\\\"height:399px; width:710px\\\" \\/><\\/p>\\r\\n\\r\\n<p><em>C\\u1ea7u B&agrave;n Tay B&agrave; N&agrave; Hills.<\\/em><\\/p>\\r\\n\\r\\n<p>- Tham gia vui ch\\u01a1i t\\u1ea1i c&ocirc;ng vi&ecirc;n Fantasy Park: v\\u1edbi c&aacute;c tr&ograve; ch\\u01a1i phi&ecirc;u l\\u01b0u m\\u1edbi l\\u1ea1 nh\\u01b0: V&ograve;ng Quay T&igrave;nh Y&ecirc;u, Phi C&ocirc;ng Skiver, \\u0110\\u01b0\\u1eddng \\u0110ua L\\u1eeda, Ng&ocirc;i Nh&agrave; Ma v&agrave; Khu tr\\u01b0ng b&agrave;y h\\u01a1n 40 t\\u01b0\\u1ee3ng s&aacute;p nh\\u1eefng nh&acirc;n v\\u1eadt n\\u1ed5i ti\\u1ebfng tr&ecirc;n th\\u1ebf gi\\u1edbi&hellip;<\\/p>\\r\\n\\r\\n<p><strong>Tr\\u01b0a:<\\/strong>&nbsp;\\u0102n tr\\u01b0a nh&agrave; h&agrave;ng.<\\/p>\\r\\n\\r\\n<p><strong>Chi\\u1ec1u:<\\/strong>&nbsp;Kh\\u1edfi h&agrave;nh \\u0111i&nbsp;<strong>C\\u1ed1 \\u0110&ocirc; Hu\\u1ebf<\\/strong>&nbsp;- Di s\\u1ea3n v\\u0103n ho&aacute; Th\\u1ebf Gi\\u1edbi, xuy&ecirc;n h\\u1ea7m \\u0111\\u01b0\\u1eddng b\\u1ed9&nbsp;<strong>\\u0111&egrave;o H\\u1ea3i V&acirc;n<\\/strong>, d\\u1eebng ch&acirc;n t\\u1ea1i&nbsp;<strong>l&agrave;ng ch&agrave;i L\\u0103ng C&ocirc;<\\/strong>&nbsp;ch\\u1ee5p h&igrave;nh l\\u01b0u ni\\u1ec7m.&nbsp;Nh\\u1eadn ph&ograve;ng kh&aacute;ch s\\u1ea1n 4* ngh\\u1ec9 ng\\u01a1i.<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"https:\\/\\/cdn2.ivivu.com\\/2018\\/12\\/06\\/17\\/ivivu-lang-co-beach.jpg\\\" style=\\\"height:400px; width:710px\\\" \\/><\\/p>\\r\\n\\r\\n<p><em>L&agrave;ng Ch&agrave;i L\\u0103ng C&ocirc;.<\\/em><\\/p>\\r\\n\\r\\n<p><strong>T\\u1ed1i:<\\/strong>&nbsp;\\u0102n t\\u1ed1i nh&agrave; h&agrave;ng v\\u1edbi \\u0111\\u1eb7c s\\u1ea3n x\\u1ee9 Hu\\u1ebf (B&aacute;nh b&egrave;o, l\\u1ecdc, n\\u1eadm, kho&aacute;i,...). Qu&yacute; kh&aacute;ch xu&ocirc;\\u0301ng thuy&ecirc;\\u0300n R&ocirc;\\u0300ng \\u0111&ecirc;\\u0309 th\\u01b0\\u01a1\\u0309ng th\\u01b0\\u0301c&nbsp;<strong>Ca Hu&ecirc;\\u0301 tr&ecirc;n s&ocirc;ng H\\u01b0\\u01a1ng<\\/strong>&nbsp;&ndash; ne\\u0301t v\\u0103n ho\\u0301a \\u0111&ocirc;\\u0323c \\u0111a\\u0301o cu\\u0309a x\\u01b0\\u0301 Hu&ecirc;\\u0301<\\/p>\\r\\n\\r\\n<h3>NG&Agrave;Y 3: HU\\u1ebe - TH&Aacute;NH \\u0110\\u1ecaA LA VANG &ndash; \\u0110\\u1ed8NG PHONG NHA (\\u0102N S&Aacute;NG, TR\\u01afA, T\\u1ed0I) &nbsp; &nbsp;&nbsp;<\\/h3>\\r\\n\\r\\n<p><strong>S&aacute;ng:<\\/strong>&nbsp;\\u0110i\\u1ec3m t&acirc;m s\\u1edbm. R\\u1eddi Hu\\u1ebf \\u0111i&nbsp;<strong>Phong Nha - K\\u1ebb B&agrave;ng<\\/strong>. Tr&ecirc;n \\u0111\\u01b0\\u1eddng, vi\\u1ebfng th\\u0103m&nbsp;<strong>Th&aacute;nh \\u0110\\u1ecba La Vang<\\/strong>&nbsp;(\\u0110\\u01b0\\u1ee3c phong t\\u1eb7ng l&agrave; Ti\\u1ec3u V\\u01b0\\u01a1ng Cung Th&aacute;nh \\u0110\\u01b0\\u1eddng) v&agrave; ngang qua&nbsp;<strong>V\\u0129 tuy\\u1ebfn 17 S&ocirc;ng B\\u1ebfn H\\u1ea3i - C\\u1ea7u Hi\\u1ec1n L\\u01b0\\u01a1ng<\\/strong>&nbsp;huy&ecirc;\\u0300n thoa\\u0323i.<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"https:\\/\\/cdn2.ivivu.com\\/2016\\/08\\/25\\/16\\/ivivu-tour-da-nang-3n2d-ba-na-hue-dong-phong-nha-thanh-dia-la-vang.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<p><em>Th&aacute;nh \\u0110\\u1ecba La Vang.<\\/em><\\/p>\\r\\n\\r\\n<p><strong>Tr\\u01b0a:<\\/strong>&nbsp;\\u0102n tr\\u01b0a nh&agrave; h&agrave;ng t\\u1ea1i Phong Nha.<\\/p>\\r\\n\\r\\n<p><strong>Chi\\u1ec1u:<\\/strong>&nbsp;Ng\\u1ed3i thuy\\u1ec1n ng\\u01b0\\u1ee3c s&ocirc;ng Son chinh ph\\u1ee5c \\u0110\\u1ed9ng Phong Nha: C&ocirc; Ti&ecirc;n &amp; Cung \\u0110&igrave;nh d\\u01b0\\u1edbi s&acirc;u l&ograve;ng n&uacute;i n\\u01a1i c&oacute; con s&ocirc;ng ng\\u1ea7m t\\u1eeb L&agrave;o ch\\u1ea3y sang, chi&ecirc;m ng\\u01b0\\u1ee1ng c&aacute;c kh\\u1ed1i th\\u1ea1ch nh\\u0169 tuy\\u1ec7t \\u0111\\u1eb9p \\u0111\\u01b0\\u1ee3c ki\\u1ebfn t\\u1ea1o b\\u1edfi thi&ecirc;n nhi&ecirc;n qua h&agrave;ng ng&agrave;n thi&ecirc;n ni&ecirc;n k\\u1ef7. Kh\\u1edfi h&agrave;nh v\\u1ec1 Hu\\u1ebf theo \\u0111\\u01b0\\u1eddng Tr\\u01b0\\u1eddng S\\u01a1n - H\\u1ed3 Ch&iacute; Minh.<\\/p>\\r\\n\\r\\n<p><em>Ho\\u1eb7c:<\\/em>&nbsp;l\\u1ef1a ch\\u1ecdn ng\\u1ed3i xe \\u0111i\\u1ec7n tham quan chinh ph\\u1ee5c \\u0110\\u1ed9ng Thi&ecirc;n \\u0110\\u01b0\\u1eddng - Ho&agrave;ng cung l&ograve;ng \\u0111\\u1ea5t s\\u1edf h\\u1eefu k\\u1ef3 quan tr&aacute;ng l\\u1ec7 v&agrave; huy\\u1ec1n \\u1ea3o b\\u1eadc nh\\u1ea5t th\\u1ebf gi\\u1edbi. Ph\\u1ee5 thu th&ecirc;m 100.000 vn\\u0111\\/kh&aacute;ch ch&ecirc;nh l\\u1ec7ch v&eacute; tham quan).<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"https:\\/\\/cdn2.ivivu.com\\/2016\\/08\\/25\\/17\\/ivivu-tour-da-nang-4n3d-hoi-an-ba-na-hue-dong-phong-nha-dong-phong-nha.jpg\\\" style=\\\"height:399px; width:710px\\\" \\/><\\/p>\\r\\n\\r\\n<p><em>\\u0110\\u1ed9ng Thi&ecirc;n \\u0110\\u01b0\\u1eddng.<\\/em><\\/p>\\r\\n\\r\\n<p><strong>T\\u1ed1i:<\\/strong>&nbsp;\\u0102n t\\u1ed1i nh&agrave; h&agrave;ng, t\\u1ef1 do kh&aacute;m ph&aacute; C\\u1ed1 \\u0110&ocirc; v\\u1ec1 \\u0111&ecirc;m v\\u1edbi&nbsp;<strong>c\\u1ea7u Tr&agrave;ng Ti\\u1ec1n<\\/strong>&nbsp;r\\u1ef1c r\\u1ee1 soi m&igrave;nh xu\\u1ed1ng d&ograve;ng&nbsp;<strong>s&ocirc;ng H\\u01b0\\u01a1ng<\\/strong>&nbsp;th\\u01a1 m\\u1ed9ng, d\\u1ea1o ch\\u01a1i ch\\u1ee3 \\u0111&ecirc;m t&igrave;m hi\\u1ec3u n&eacute;t sinh ho\\u1ea1t c\\u1ee7a \\u0111\\u1ea5t kinh th&agrave;nh. Ngh\\u1ec9 kh&aacute;ch s\\u1ea1n 4*.<\\/p>\\r\\n\\r\\n<h3>NG&Agrave;Y 4:&nbsp;L\\u0102NG KH\\u