--
-- Cấu trúc bảng cho bảng `tours`
--

CREATE TABLE `tours` (
  `id` int(10) UNSIGNED NOT NULL,
  `tour_code` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `count_people` int(11) NOT NULL,
  `tour_name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `departure_location` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `destination` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `province_id` int(10) UNSIGNED NOT NULL,
  `price_adult` double NOT NULL,
  `price_kid` double NOT NULL,
  `single_room_price` double NOT NULL,
  `tour_detail` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_program` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_note` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `number_of_day` int(11) NOT NULL,
  `departure_time` time NOT NULL,
  `departure_date` date NOT NULL,
  `return_date` date NOT NULL,
  `vehicle` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_image` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `discount` int(11) DEFAULT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '0',
  `isdeleted` tinyint(1) NOT NULL DEFAULT '0',
  `created_by` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `updated_by` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tours`
--

INSERT INTO `tours` (`id`, `tour_code`, `count_people`, `tour_name`, `departure_location`, `destination`, `province_id`, `price_adult`, `price_kid`, `single_room_price`, `tour_detail`, `tour_program`, `tour_note`, `number_of_day`, `departure_time`, `departure_date`, `return_date`, `vehicle`, `tour_image`, `discount`, `status`, `isdeleted`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
(6, 'J8Hz4KSDGn', 20, 'Tour Miền Bắc 5N4D: Sài Gòn - Hà Nội - SaPa - Hạ Long', 'Sài Gòn', 'Hạ Long', 4, 6290000, 4290000, 2290000, '<h3>H&agrave;nh Tr&igrave;nh Trải Nghiệm Du Lịch Miền Bắc</h3>\r\n\r\n<p>Miền Bắc Việt Nam gồm T&acirc;y Bắc, Đ&ocirc;ng Bắc v&agrave; đồng bằng S&ocirc;ng Hồng, l&agrave; c&aacute;i n&ocirc;i văn h&oacute;a lịch sử h&agrave;ng ng&agrave;n năm của Việt Nam. Thi&ecirc;n nhi&ecirc;n v&agrave; cảnh đẹp h&ugrave;ng vĩ, 4 m&ugrave;a xu&acirc;n hạ thu đ&ocirc;ng, miền Bắc lu&ocirc;n l&agrave; điểm hẹn hấp dẫn cho&nbsp;qu&yacute; kh&aacute;ch muốn đến v&agrave; đặt ch&acirc;n nhiều lần. Mỗi v&ugrave;ng, mỗi tỉnh, mỗi điểm sẽ l&agrave; một v&ugrave;ng văn h&oacute;a, một di sản, một thắng cảnh,...mang vẻ đẹp ri&ecirc;ng biệt kh&aacute;c nhau, l&agrave;m cho du kh&aacute;ch lẫn trong v&agrave; ngo&agrave;i nước đều muốn chinh phục, muốn kh&aacute;m ph&aacute;. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; h&agrave;nh tr&igrave;nh th&uacute; vị ngay h&ocirc;m nay!&nbsp;</p>\r\n\r\n<p><em><strong>Đặt đo&agrave;n ri&ecirc;ng li&ecirc;n hệ 028 3933 8002 hoặc hotline 0906 355 542 để c&oacute; gi&aacute; tốt.</strong></em></p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Li&ecirc;n tuyến h&agrave;nh tr&igrave;nh qu&yacute; kh&aacute;ch sẽ đi từ bất ngờ n&agrave;y sang bất ngờ kh&aacute;c:&nbsp;</p>\r\n\r\n<p>- Cột mốc hữu nghị&nbsp;<strong>Việt Nam &ndash; Trung Quốc</strong>.</p>\r\n\r\n<p>-<strong>&nbsp;Bản C&aacute;t C&aacute;t - S&iacute;n Chải</strong>&nbsp;của người<strong>&nbsp;H&rsquo;mong</strong>.</p>\r\n\r\n<p>- Chinh phục&nbsp;<strong>đỉnh Fanxiphan -&nbsp;</strong>Nốc Nh&agrave; Của Đ&ocirc;ng Dương<strong>.</strong></p>\r\n\r\n<p><strong>-</strong>&nbsp;Danh thắng&nbsp;<strong>Tr&agrave;ng An &ndash;&nbsp;</strong>Di sản được UNESCO c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n Thế giới.</p>\r\n\r\n<p>- Tham quan Di sản thi&ecirc;n nhi&ecirc;n Thế giới -&nbsp;<strong>Vịnh Hạ Long</strong>.</p>', '<h3>NG&Agrave;Y 01: TP HỒ CH&Iacute; MINH - SAPA ( ĂN TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: 05h30 Nh&acirc;n vi&ecirc;n đ&oacute;n đo&agrave;n tại s&acirc;n bay&nbsp;<strong>T&acirc;n Sơn Nhất</strong>&nbsp;l&agrave;m thủ tục bay đi&nbsp;<strong>H&agrave; Nội</strong>&nbsp;( chuyến bay của&nbsp;<strong>Viet Jet Air VJ 128</strong>&nbsp;l&uacute;c&nbsp;<strong>07h55</strong>). Đến s&acirc;n bay Nội B&agrave;i, xe khởi h&agrave;nh đưa đo&agrave;n đi&nbsp;<strong>Sapa</strong>.</p>\r\n\r\n<p>Đo&agrave;n d&ugrave;ng cơm trưa tại&nbsp;<strong>L&agrave;o Cai</strong>.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch tự do tham quan v&agrave; chụp h&igrave;nh tại&nbsp;<strong>cột mốc hữu nghị</strong>&nbsp;<strong>Việt Nam &ndash; Trung Quốc</strong>,</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-cua-khau-huu-nghi.gif\" /></p>\r\n\r\n<p><em>Cột mốc Hữu Nghị.</em></p>\r\n\r\n<p>Mua sắm đồ lưu niệm v&agrave; điện tử tại chợ<strong>&nbsp;Cốc Lếu L&agrave;o Cai</strong>&nbsp;( nếu c&ograve;n thời gian).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-cho-coc-leu-lao-cai.gif\" />&nbsp;</p>\r\n\r\n<p><em>Chợ Cốc Lếu L&agrave;o Cai.</em></p>\r\n\r\n<p>Sau đ&oacute; khởi h&agrave;nh đi&nbsp;<strong>Sapa</strong>&nbsp;nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p>Tối: Qu&yacute; kh&aacute;ch ăn t&ocirc;́i v&agrave; nghỉ đ&ecirc;m tại&nbsp;<strong>SaPa</strong>.</p>\r\n\r\n<h3>NG&Agrave;Y 02: KH&Aacute;M PH&Aacute; SAPA ( ĂN S&Aacute;NG, TRƯA, ĂN TỐI TỰ T&Uacute;C)</h3>\r\n\r\n<p>S&aacute;ng: Điểm t&acirc;m s&aacute;ng, Xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch đến bản&nbsp;<strong>C&aacute;t C&aacute;t</strong>, bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; bản l&agrave;ng</p>\r\n\r\n<p>- Tham quan&nbsp;<strong>bản C&aacute;t C&aacute;t - S&iacute;n Chải</strong>&nbsp;của người&nbsp;<strong>H&rsquo;mong</strong>. Qu&yacute; kh&aacute;ch sẽ được thăm v&agrave; t&igrave;m hiểu phong tục tập qu&aacute;n sinh hoạt kỳ th&uacute;, đơn sơ đến b&igrave;nh dị của những người d&acirc;n tộc thiểu số.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-cat-cat-sin-chai.gif\" /></p>\r\n\r\n<p><em>Bản C&aacute;t C&aacute;t - S&iacute;n Chải.</em></p>\r\n\r\n<p>- Tham quan th&aacute;c&nbsp;<strong>C&aacute;t C&aacute;t</strong>&nbsp;v&agrave; nh&agrave; m&aacute;y thuỷ điện do người Ph&aacute;p x&acirc;y dựng đầu thế kỷ XX, vui chơi v&agrave; chụp h&igrave;nh lưu niệm&hellip;</p>\r\n\r\n<p>Chiều: Qu&yacute; kh&aacute;ch tham quan v&agrave; tự do mua sắm đồ lưu niệm, đặc sản tại&nbsp;<strong>Chợ Sa Pa</strong>&nbsp;- nơi trao đổi mua b&aacute;n nhiều loại h&agrave;ng h&oacute;a, sản phẩm địa phương.</p>\r\n\r\n<p>-&nbsp;<strong>Nh&agrave; Thờ Đ&aacute;</strong>&nbsp;- dấu ấn kiến tr&uacute;c của người Ph&aacute;p c&ograve;n lại vẹn to&agrave;n nhất tại&nbsp;<strong>Sa Pa</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/17/14/ivivu-nha-tho-da-sapa2.jpg\" /></p>\r\n\r\n<p><em>Nh&agrave; Thờ Đ&aacute;.</em></p>\r\n\r\n<p>Nếu Q&uacute;y kh&aacute;ch muốn chinh phục&nbsp;<strong>đỉnh Fanxiphan</strong>, Cty sẽ bố tr&iacute; thời gian tham quan cho ph&ugrave; hợp ( phụ thu th&ecirc;m 850.000đ/kh&aacute;ch&nbsp; bao gồm tiền c&aacute;p treo + xe di chuyển ra ga c&aacute;p).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/01/02/16/ivivu-chinh-phuc-fansipan.jpg\" /></p>\r\n\r\n<p><em>Đỉnh Fanxiphan.</em></p>\r\n\r\n<p>Hoặc Qu&yacute; kh&aacute;ch tham quan c&acirc;y&nbsp;<strong>cầu k&iacute;nh Rồng M&acirc;y</strong>&nbsp;(phụ thu th&ecirc;m chi ph&iacute; 400.000 đồng/người lớn v&agrave; 200.000 đồng trẻ em).</p>\r\n\r\n<p>Tối: Đo&agrave;n tự t&uacute;c ăn tối thưởng thức ẩm thực v&ugrave;ng cao: cơm lam, g&agrave; nướng, thắng cố, rượu Ng&ocirc;&hellip;tại chợ đ&ecirc;m Sapa.&nbsp;</p>\r\n\r\n<h3>NG&Agrave;Y 03: SAPA&nbsp; - H&Agrave; NỘI ( ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m v&agrave; trả ph&ograve;ng kh&aacute;ch sạn, tự do tham quan v&agrave; mua sắm đặc sản về l&agrave;m qu&agrave; cho người th&acirc;n tại chợ Sapa, sau đ&oacute; xe đưa đo&agrave;n khởi h&agrave;nh về lại&nbsp;<strong>L&agrave;o Cai</strong>,</p>\r\n\r\n<p>Đo&agrave;n d&ugrave;ng cơm trưa tại&nbsp;<strong>L&agrave;o Cai</strong>.</p>\r\n\r\n<p>Chiều: Xe đưa đo&agrave;n về lại H&agrave; Nội, Qu&yacute; kh&aacute;ch tham quan:</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch gh&eacute; thăm&nbsp;<strong>Văn Miếu - Quốc Tử Gi&aacute;m</strong>&nbsp;&ndash; Nơi đựoc xem như Trường Đại học đầu ti&ecirc;n của&nbsp;<strong>Việt Nam</strong>&nbsp;với 82 tấm bia Tiến sỹ c&ograve;n lưu danh sử s&aacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/31/14/ivivu-tour-mien-bac-5n4d-ha-noi-sapa-ha-long-van-mieu-quoc-tu-giam.jpg\" /></p>\r\n\r\n<p><em>Văn Miếu - Quốc Tử Gi&aacute;m.</em></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối thưởng thức ẩm thực&nbsp;<strong>H&agrave; Nội</strong>: B&aacute;nh t&ocirc;m hồ t&acirc;y, chả c&aacute;&hellip; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>Tối: Qu&yacute; kh&aacute;ch tự do tham quan :</p>\r\n\r\n<p>Tham quan&nbsp;<strong>Đền Ngọc Sơn, Cầu Th&ecirc; H&uacute;c, hồ Ho&agrave;n Kiếm, Nh&agrave; H&aacute;t Lớn H&agrave; Nội</strong>, Nh&agrave; Thờ Lớn H&agrave; Nội&hellip;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/09/06/09/ivivu-tour-mien-bac-4n3d-ha-noi-ha-long-bai-dinh-trang-an-den-ngoc-son.jpg\" /></p>\r\n\r\n<p><em>Cầu Th&ecirc; H&uacute;c.</em></p>\r\n\r\n<p>Những khu phố ẩm thực trứ danh đất&nbsp;<strong>H&agrave; Th&agrave;nh</strong>: phố Cấm Chỉ, Tạ Hiện&hellip;</p>\r\n\r\n<p>Đo&agrave;n nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n\r\n<h3>NG&Agrave;Y 04: H&Agrave; NỘI&nbsp; - KDL TR&Agrave;NG AN - CH&Ugrave;A B&Aacute;I Đ&Iacute;NH ( ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Qu&yacute; kh&aacute;ch d&ugrave;ng ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn, Hướng dẫn vi&ecirc;n đưa đo&agrave;n xe đưa đo&agrave;n tham quan cụm di t&iacute;ch lịch sử Phủ Chủ Tịch. Với c&aacute;c quần thể kiến tr&uacute;c:</p>\r\n\r\n<p>-&nbsp;<strong>Quảng Trường Ba Đ&igrave;nh</strong>&nbsp;&ndash; l&agrave; một di t&iacute;ch đặc biệt, nơi đ&acirc;y c&ograve;n l&agrave; nơi ghi nhận nhiều dấu ấn quan trọng tong lịch sử&nbsp;<strong>Việt Nam</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/12/09/16/ivivu-quang-truong-ba-dinh.gif\" /></p>\r\n\r\n<p><em>Quảng Trường B&agrave; Đ&igrave;nh.</em></p>\r\n\r\n<p>- Bảo t&agrave;ng&nbsp;<strong>Hồ Ch&iacute; Minh</strong>&nbsp;- Nằm trong khu vực c&oacute; nhiều di t&iacute;ch như:&nbsp;<strong>Lăng Chủ tịch Hồ Ch&iacute; Minh</strong>, Khu di t&iacute;ch Phủ chủ tịch, Ch&ugrave;a Một Cột... tạo th&agrave;nh một quần thể c&aacute;c di t&iacute;ch đặc biệt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/12/14/16/ivivu-lang-chu-tich-ho-chi-minh.jpg\" /></p>\r\n\r\n<p><em>Lăng Chủ tịch Hồ Ch&iacute; Minh.</em></p>\r\n\r\n<p>- Nh&agrave; S&agrave;n&nbsp;giản dị nằm giữa thủ đ&ocirc;&nbsp;<strong>H&agrave; Nội</strong>&nbsp;l&agrave; nơi<strong>&nbsp;B&aacute;c Hồ</strong>&nbsp;đ&atilde; sống v&agrave; l&agrave;m việc l&acirc;u nhất trong cuộc đời hoạt động c&aacute;ch mạng của m&igrave;nh.</p>\r\n\r\n<p>-&nbsp;<strong>Ch&ugrave;a Một Cột (Ch&ugrave;a Mật)</strong>&nbsp;- ng&ocirc;i ch&ugrave;a c&oacute; kiến tr&uacute;c độc đ&aacute;o, chỉ c&oacute; 1 gian dựng tr&ecirc;n 1 cột đ&aacute; nằm giữa l&ograve;ng hồ Linh Chiểu&hellip;..</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/02/21/16/ivivu-tour-ha-noi-city-tour-chua-mot-cot-1.jpg\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Một Cột.</em></p>\r\n\r\n<p>- Tiếp tục h&agrave;nh tr&igrave;nh, đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Ninh B&igrave;nh.&nbsp;</strong>Đến&nbsp;<strong>Ninh B&igrave;nh</strong>&nbsp;Qu&yacute; kh&aacute;ch cầu lễ phật tại&nbsp;<strong>Ch&ugrave;a B&aacute;i Đ&iacute;nh</strong>&nbsp;(kh&ocirc;ng bao gồm xe điện đưa đ&oacute;n l&ecirc;n ch&ugrave;a ), ng&ocirc;i ch&ugrave;a lớn nhất Việt Nam với 500 pho tượng La H&aacute;n, tượng phật đồng lớn nhất&nbsp;<strong>Việt Nam</strong>&nbsp;cao 10m nặng 100 tấn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/09/13/16/ivivu-tour-mien-bac-4n3d-ha-noi-suoi-giang-tu-le-mu-cang-chai-chua-bai-dinh.jpg\" /></p>\r\n\r\n<p><em>Ch&ugrave;a B&aacute;i Đ&iacute;nh.</em></p>\r\n\r\n<p>D&ugrave;ng cơm trưa với đặc sản Cơm Ch&aacute;y- D&ecirc; n&uacute;i của&nbsp;<strong>Ninh B&igrave;nh</strong>.</p>\r\n\r\n<p>Chiều: Qu&yacute; kh&aacute;ch tham Quần thể&nbsp;<strong>Danh thắng Tr&agrave;ng An</strong>&nbsp;&ndash;&nbsp;<strong>Di sản được UNESCO</strong>&nbsp;c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n Thế giới, gi&uacute;p cho&nbsp;<strong>Việt Nam</strong>&nbsp;lần dầu lọt v&agrave;o danh s&aacute;ch 30 di sản hỗn hợp&nbsp;<strong>Thế Giới</strong>. L&ecirc;n thuyền xu&ocirc;i d&ograve;ng S&agrave;o kh&ecirc; uốn lượn giữa c&aacute;nh đồng l&uacute;a, nơi những dải đ&aacute; v&ocirc;i, thung lũng v&agrave; những s&ocirc;ng ng&ograve;i đan xen tạo n&ecirc;n một kh&ocirc;ng gian huyền ảo, kỳ b&iacute; , qu&yacute; kh&aacute;ch đi đ&ograve; tham Hang s&aacute;ng , Hang tối , Hang nấu rượu&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/09/25/11/ivivu-trang-an.jpg\" /></p>\r\n\r\n<p><em>Danh thắng Tr&agrave;ng An.</em></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch rời&nbsp;<strong>Ninh B&igrave;nh</strong>, theo Quốc lộ 10 đi Hạ Long, tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch sẽ được cảm nhận, kh&aacute;m ph&aacute; cuộc sống, phong cảnh đặc trưng của<strong>&nbsp;Đồng Bằng Bắc Bộ</strong>&nbsp;với cảnh l&agrave;ng qu&ecirc; thanh b&igrave;nh qua c&aacute;c tỉnh&nbsp;<strong>Nam Định, Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng</strong>. Đến&nbsp;<strong>Hạ Long</strong>, nhận ph&ograve;ng, nghỉ ngơi, ăn tối tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>Tối: Qu&yacute; kh&aacute;ch tự do dạo chơi C&ocirc;ng vi&ecirc;n Ho&agrave;ng Gia dọc biển&nbsp;<strong>B&atilde;i Ch&aacute;y</strong>, đi mua sắm h&agrave;ng h&oacute;a tại khu&nbsp;<strong>Chợ Đ&ecirc;m Hạ Long</strong>&nbsp;sầm uất với c&aacute;c sản phẩm đặc trưng&nbsp;<strong>Hạ Long</strong>, l&ecirc;n cầu&nbsp;<strong>B&atilde;i Ch&aacute;y</strong>&nbsp;&ndash; c&acirc;y cầu d&acirc;y văng d&agrave;i nhất&nbsp;<strong>Việt Nam</strong>&nbsp;ngắm&nbsp;<strong>Hạ Long</strong>&nbsp;về đ&ecirc;m.</p>\r\n\r\n<h3>NG&Agrave;Y 05: HẠ LONG &ndash; HẢI DƯƠNG &ndash; S&Acirc;N BAY NỘI B&Agrave;I ( ĂN S&Aacute;NG, TRƯA, ĂN TỐI NHẸ TẠI S&Acirc;N BAY)</h3>\r\n\r\n<p>S&aacute;ng: Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn, Đo&agrave;n đến bến thuyền&nbsp;<strong>Tuần Ch&acirc;u</strong>&nbsp;bắt đầu h&agrave;nh tr&igrave;nh tham quan Di sản thi&ecirc;n nhi&ecirc;n Thế giới tại&nbsp;<strong>Việt Nam</strong>, với c&aacute;c danh thắng nổi tiếng:</p>\r\n\r\n<p>Tham&nbsp;<strong>Động Thi&ecirc;n Cung</strong>, ngắm cảnh&nbsp;<strong>L&agrave;ng Ch&agrave;i, h&ograve;n Ấm, h&ograve;n R&ugrave;a, h&ograve;n Đỉnh Hương, h&ograve;n Ch&oacute; Đ&aacute;, h&ograve;n G&agrave; Chọi</strong>&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-hon-trong-mai.gif\" />&nbsp;</p>\r\n\r\n<p><em>H&ograve;n G&agrave; Chọi.</em></p>\r\n\r\n<p>Chiều: T&agrave;u về cập bến, xe đưa đo&agrave;n khởi h&agrave;nh về lại&nbsp;<strong>H&agrave; Nội</strong>, tr&ecirc;n đường đo&agrave;n dừng ch&acirc;n tham quan v&agrave; mua sắm đặc sản b&aacute;nh đậu xanh tại&nbsp;<strong>Hải Dương</strong>. Về tới Nội B&agrave;i, Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối (ăn nhẹ tại s&acirc;n bay). Hướng dẫn vi&ecirc;n l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay của&nbsp;<strong>VJ173</strong>&nbsp;bay về lại&nbsp;<strong>Tp HCM</strong>&nbsp;l&uacute;c 20h45, chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh tham quan&nbsp;<strong>Miền Bắc</strong>.</p>', '<p>- Trong những trường hợp bất khả kh&aacute;ng như: khủng bố, bạo động, thi&ecirc;n tai, lũ lụt. dịch bệnh&hellip; Tuỳ theo t&igrave;nh h&igrave;nh thực tế v&agrave; sự thuận tiện, an to&agrave;n của kh&aacute;ch h&agrave;ng,&nbsp;c&ocirc;ng ty&nbsp;sẽ chủ động th&ocirc;ng b&aacute;o cho kh&aacute;ch h&agrave;ng sự thay đổi như sau: huỷ hoặc thay thế bằng một chương tr&igrave;nh mới với chi ph&iacute; tương đương chương tr&igrave;nh tham quan trước đ&oacute;. Trong trường hợp chương tr&igrave;nh mới c&oacute; ph&aacute;t sinh th&igrave; Kh&aacute;ch h&agrave;ng sẽ thanh to&aacute;n khoản ph&aacute;t sinh n&agrave;y. Tuy nhi&ecirc;n, mỗi b&ecirc;n c&oacute; tr&aacute;ch nhiệm cố gắng tối đa, gi&uacute;p đỡ b&ecirc;n bị thiệt hại nhằm giảm thiểu c&aacute;c tổn thất g&acirc;y ra v&igrave; l&yacute; do bất khả kh&aacute;ng.&hellip;</p>\r\n\r\n<p>- Khi Qu&yacute; kh&aacute;ch đăng k&iacute; tour, x&aacute;c nhận sự ch&iacute;nh x&aacute;c về họ, t&ecirc;n (đ&uacute;ng từng k&yacute; tự ghi trong hộ chiếu hoặc CMND), ng&agrave;y-th&aacute;ng-năm sinh.. của h&agrave;nh kh&aacute;ch theo y&ecirc;u cầu của h&atilde;ng vận chuyển.</p>\r\n\r\n<p>- Do đặc điểm địa h&igrave;nh, tại Sapa qu&yacute; kh&aacute;ch chủ yếu đi bộ để tham quan, v&igrave; thế qu&yacute; kh&aacute;ch vui l&ograve;ng lựa chọn trang phục ph&ugrave; hợp.</p>\r\n\r\n<p>- Qu&yacute; kh&aacute;ch tr&ecirc;n 70 tuổi hoặc phụ nữ c&oacute; Thai dưới 06 Th&aacute;ng (V&igrave; l&yacute; do sức khỏe của Thai phụ, ch&uacute;ng t&ocirc;i từ chối cung cấp dịch vụ tour cho Phụ nữ mang thai tr&ecirc;n 06 th&aacute;ng) khi đi phải c&oacute; giấy x&aacute;c nhận đảm bảo sức khỏe của Bệnh viện cấp Quận/ Huyện trở l&ecirc;n.</p>\r\n\r\n<p>- Khi đi du lịch Qu&yacute; kh&aacute;ch dưới 14 tuổi mang theo giấy khai sinh bản ch&iacute;nh hoặc hộ chiếu c&ograve;n hạn, Tr&ecirc;n&nbsp; 14 tuổi phải đem theo giấy CMND, Hộ Chiếu, CCCD bản ch&iacute;nh, h&igrave;nh ảnh r&otilde;, c&ograve;n hạn sử dụng trong v&ograve;ng 15 năm kể từ ng&agrave;y cấp hoặc Hộ Chiếu c&ograve;n hạn.</p>\r\n\r\n<p>- Ri&ecirc;ng kh&aacute;ch Việt Kiều phải mang theo Hộ Chiếu hoặc thẻ xanh k&egrave;m theo Visa v&agrave; giấy t&aacute;i xuất nhập Việt Nam l&agrave;m thủ tục l&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p>- H&agrave;nh l&yacute; s&aacute;ch tay: Tối đa 07 k&yacute; x&aacute;ch tay /kh&aacute;ch, kh&ocirc;ng để c&aacute;c vật dụng sắc nhọn, chất dễ ch&aacute;y .</p>\r\n\r\n<p>- H&agrave;nh l&yacute; k&yacute; gửi: tối đa 20kg /kh&aacute;ch, kh&ocirc;ng để m&aacute;y chụp ảnh, m&aacute;y t&iacute;nh s&aacute;ch tay, tư trang, tiền bạc c&oacute; gi&aacute; trị v&agrave;o h&agrave;nh l&yacute; k&yacute; gửi.</p>', 5, '19:50:00', '2020-11-23', '2020-11-28', 'Ô tô', '2020-Nov-Sat_1605945168314.jpg', 0, 0, 0, '2', '2', '2020-11-21 00:52:48', '2020-11-21 00:52:48'),
(7, 'hbaz6YXxvE', 20, 'Tour Miền Bắc 4N3D: HCM - Hạ Long - Ninh Bình - Sapa - Hà Nội', 'Hồ Chí Minh', 'Hà Nội', 4, 7390000, 4390000, 2390000, '<h3>Trời Nam Thương Nhớ Đất Thăng Long</h3>\r\n\r\n<p>Xứ Bắc, Miền Bắc &ndash; v&ugrave;ng đất khai cơ của c&aacute;c Vương triều Việt, nơi định đ&ocirc; của hầu hết c&aacute;c triều đại phong kiến Việt Nam ch&iacute;nh v&igrave; vậy nơi đ&acirc;y được xem như l&agrave; đất kinh đ&ocirc; ng&agrave;n năm văn hiến với một bề d&agrave;y văn h&oacute;a, lịch sử s&acirc;u sắc v&agrave; đa dạng. Tuy nhi&ecirc;n, khi đến đ&acirc;y, Du kh&aacute;ch kh&ocirc;ng chỉ được tham quan những c&ocirc;ng tr&igrave;nh văn h&oacute;a - lịch sử cổ k&iacute;nh như: Đền H&ugrave;ng, Văn Miếu, Ho&agrave;ng th&agrave;nh Thăng Long, Cố đ&ocirc; Hoa Lư, Ch&ugrave;a B&aacute;i Đ&iacute;nh&hellip;, trải nghiệm những n&eacute;t văn h&oacute;a đặc sắc của đất kinh đ&ocirc; như&nbsp;: ngoạn cảnh 36 phố phường, thưởng thức ẩm thực, xem m&uacute;a rối nước&hellip;; m&agrave; c&ograve;n c&oacute; thể thăm th&uacute; c&aacute;c thắng cảnh nổi tiếng nơi đ&acirc;y như&nbsp;: Di sản thế giới Danh thắng Tr&agrave;ng An v&agrave; Vịnh Hạ Long...Ngo&agrave;i ra Du kh&aacute;ch c&ograve;n c&oacute; thể h&ograve;a m&igrave;nh v&agrave;o cuộc sống b&igrave;nh dị nhưng đậm bản sắc của cộng đồng d&acirc;n tộc &iacute;t người v&ugrave;ng cao như&nbsp;: Th&aacute;i, Hmong, Dao, Mường, T&agrave;y&hellip; C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Ai về xứ Bắc cũng sẽ chi&ecirc;m ngưỡng, cảm nhận c&aacute;c điểm đầy th&uacute; vị:&nbsp;</p>\r\n\r\n<p>- Tham quan danh lam thắng cảnh miền Bắc.</p>\r\n\r\n<p>- H&agrave;nh hương viếng ch&ugrave;a&nbsp;<strong>B&aacute;i Đ&iacute;nh</strong>. Thưởng ngoạn danh thắng&nbsp;<strong>Tr&agrave;ng An</strong>.</p>\r\n\r\n<p>- Du thuyền, chi&ecirc;m ngưỡng Hạ Long, di sản thi&ecirc;n nhi&ecirc;n thế giới.</p>\r\n\r\n<p>- Chi&ecirc;m ngưỡng&nbsp;<strong>Sa Pa</strong>&nbsp;&ndash; thị trấn trong m&acirc;y.</p>\r\n\r\n<p>- Thưởng thức hương vị c&aacute;c m&oacute;n ăn miền Bắc.</p>', '<h3>NG&Agrave;Y 1: S&Agrave;I G&Ograve;N - HẢI PH&Ograve;NG - HẠ LONG&nbsp; &nbsp;( ĂN TRƯA, CHIỀU)</h3>\r\n\r\n<p>Buổi s&aacute;ng, Nh&acirc;n vi&ecirc;n C&ocirc;ng ty du lịch sẽ l&agrave;m thủ tục chuyến bay&nbsp;<strong>S&agrave;i G&ograve;n &ndash; V&acirc;n Đồn (Quảng Ninh)</strong>&nbsp;tr&ecirc;n chuyến bay VJ230 07:00 - 09:00, đến nơi hướng dẫn vi&ecirc;n v&agrave; xe đưa Qu&yacute; kh&aacute;ch ra bến t&agrave;u, l&ecirc;n du thuyền tham quan Vịnh Hạ Long &ndash; Đệ nhất thắng cảnh của Bắc Bộ.</p>\r\n\r\n<p>Buổi trưa, Q&uacute;y kh&aacute;ch ăn trưa tr&ecirc;n t&agrave;u v&agrave; tiếp tục tham quan&nbsp;<strong>Động Thi&ecirc;n Cung</strong>, đi qua&nbsp;<strong>H&ograve;n Ch&oacute; Đ&aacute;, Lư Hương, G&agrave; Chọi</strong>. Sau đ&oacute; qu&yacute; kh&aacute;ch l&ecirc;n bờ về kh&aacute;ch sạn nhận ph&ograve;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/26/09/ivivu-dong-thien-cung.gif\" /></p>\r\n\r\n<p><em>Động Thi&ecirc;n Cung.</em></p>\r\n\r\n<p>Buổi tối, Qu&yacute; Kh&aacute;ch tự do đi dạo&nbsp;<strong>Hạ Long</strong>&nbsp;về đ&ecirc;m hoặc v&agrave;o khu&nbsp;<strong>Sunworld Hạ long</strong>, đi c&aacute;p treo Nữ Ho&agrave;ng ngắm&nbsp;<strong>Hạ Long</strong>&nbsp;- l&ecirc;n V&ograve;ng xoay Mặt Trời cao 215m, vườn Nhật&hellip; ( chi ph&iacute; tự t&uacute;c).&nbsp; Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tại&nbsp;<strong>Hạ Long</strong>.</p>\r\n\r\n<h3>NG&Agrave;Y 2: HẠ LONG - NINH B&Igrave;NH - H&Agrave; NỘI ( ĂN S&Aacute;NG,&nbsp;TRƯA, CHIỀU)&nbsp;</h3>\r\n\r\n<p>Buổi s&aacute;ng, ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng v&agrave; khởi h&agrave;nh đi&nbsp;<strong>Ninh B&igrave;nh</strong>.</p>\r\n\r\n<p>Tham quan ch&ugrave;a&nbsp;<strong>B&aacute;i Đ&iacute;nh</strong>, một ng&ocirc;i ch&ugrave;a với&nbsp;<strong>Sơn Thủy</strong>&nbsp;h&ograve;a hợp, một cảnh Non Thi&ecirc;n h&ugrave;ng vĩ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/25/15/ivivu-chua-bai-dinh.gif\" /></p>\r\n\r\n<p><em>Ch&ugrave;a B&aacute;i Đ&iacute;nh.</em></p>\r\n\r\n<p>Qu&yacute; Kh&aacute;ch viếng v&agrave; chi&ecirc;m b&aacute;i ng&ocirc;i ch&ugrave;a chiếm giữ nhiều kỷ lục nhất&nbsp;<strong>Đ&ocirc;ng Nam &Aacute;</strong>&nbsp;hiện nay.</p>\r\n\r\n<p>Buổi trưa, Q&uacute;y kh&aacute;ch ăn trưa v&agrave; tham quan Khu du lịch sinh th&aacute;i &ndash; danh thắng&nbsp;<strong>Tr&agrave;ng An</strong>&nbsp;(một quần thể n&uacute;i non, hang động, s&ocirc;ng suối được v&iacute; như&nbsp;<strong>Hạ Long</strong>&nbsp;tr&ecirc;n cạn).</p>\r\n\r\n<p>Q&uacute;y kh&aacute;ch khởi h&agrave;nh về H&agrave; Nội ăn tối nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p>Buổi tối, Qu&yacute; kh&aacute;ch tự do dạo Chợ đ&ecirc;m&nbsp;<strong>Phố cổ</strong>&nbsp;v&agrave;o tối thứ 6, 7, chủ nhật, hoặc những trung t&acirc;m thương mại lớn như: Royalcity, Timecity, Vincom, Tr&agrave;ng Tiền Plaza&hellip;. nghỉ đ&ecirc;m tại<strong>&nbsp;H&agrave; Nội</strong>.</p>\r\n\r\n<h3>NG&Agrave;Y 3: H&Agrave; NỘI - L&Agrave;O CAI - SAPA&nbsp; ( ĂN S&Aacute;NG, TRƯA, CHIỀU)</h3>\r\n\r\n<p>Buổi s&aacute;ng, ăn s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng xe đưa Đo&agrave;n đi ngang ngắm nh&igrave;n lăng B&aacute;c sau đ&oacute; khởi h&agrave;nh đi<strong>&nbsp;SaPa</strong>&nbsp;theo đường cao tốc&nbsp;<strong>Nội B&agrave;i &ndash; L&agrave;o Cai</strong>. Tr&ecirc;n đường Qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng vẽ đẹp h&ugrave;ng vĩ của n&uacute;i rừng&nbsp;<strong>T&acirc;y Bắc</strong>.</p>\r\n\r\n<p>Buổi trưa, Đến th&agrave;nh phố&nbsp;<strong>L&agrave;o Cai</strong>&nbsp;d&ugrave;ng cơm trưa v&agrave; tiếp tục h&agrave;nh tr&igrave;nh 38km sẽ đến với Sapa.</p>\r\n\r\n<p>Buổi chiều, Q&uacute;y kh&aacute;ch l&ecirc;n đỉnh&nbsp;<strong>Phanxipăng</strong>&nbsp;bằng c&aacute;p treo ba d&acirc;y c&oacute; độ ch&ecirc;nh giữa ga đi v&agrave; ga đến lớn nhất thế giới: 1.410 m&eacute;t v&agrave; c&aacute;p treo ba d&acirc;y d&agrave;i nhất thế giới: 6.292 m&eacute;t. Sau đ&oacute; Qu&yacute; kh&aacute;ch l&ecirc;n đỉnh cao 3143m (Chi ph&iacute; tự t&uacute;c v&eacute; tham khảo c&aacute;p treo 750.000vnđ/v&eacute;).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/06/15/16/ivivu-fansipang.gif\" /></p>\r\n\r\n<p><em>Fansipang.</em></p>\r\n\r\n<p>(Q&uacute;y kh&aacute;ch vui l&ograve;ng mang theo &aacute;o lạnh, mũ len v&igrave; tr&ecirc;n đỉnh&nbsp;<strong>Phanxipang</strong>&nbsp;gi&oacute; lạnh, qu&yacute; kh&aacute;ch n&agrave;o bị huyết &aacute;p, tim mạch, bệnh đường h&ocirc; hấp đươc khuyến c&aacute;o kh&ocirc;ng n&ecirc;n đi).</p>\r\n\r\n<p>Buổi tối, Qu&yacute; kh&aacute;ch đi dạo&nbsp;<strong>Sapa</strong>&nbsp;về đ&ecirc;m, tham quan&nbsp;<strong>Nh&agrave; Thờ Đ&aacute;</strong>, chợ t&igrave;nh&nbsp;<strong>Sapa</strong>&nbsp;v&agrave;o tối thứ 7 v&agrave; chủ nhật h&agrave;ng tuần, nghỉ đ&ecirc;m tại Sapa.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/17/14/ivivu-nha-tho-da-sapa8.jpg\" /></p>\r\n\r\n<p><em>Nh&agrave; Thờ Đ&aacute;.</em></p>\r\n\r\n<h3>NG&Agrave;Y 4: SAPA - NỘI B&Agrave;I - S&Agrave;I G&Ograve;N ( ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>Buổi s&aacute;ng, Qu&yacute; kh&aacute;ch chinh phục đỉnh&nbsp;<strong>H&agrave;m Rồng</strong>, l&ecirc;n đến&nbsp;<strong>Cổng Trời</strong>, h&ograve;n Phụ Tử, chi&ecirc;m ngưỡng phong cảnh Sapa trong sương mờ. Hoặc bản C&aacute;t C&aacute;t nơi sinh sống của d&acirc;n tộc H&rsquo;m&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/31/14/ivivu-tour-sapa-3n2d-lao-chai-ta-van-cat-cat-ham-rong1.jpg\" /></p>\r\n\r\n<p><em>KDL H&agrave;m Rồng.</em></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch trả ph&ograve;ng, tạm biệt&nbsp;<strong>SaPa</strong>.</p>\r\n\r\n<p>Xe đưa đo&agrave;n trở lại&nbsp;<strong>L&agrave;o Cai, gh&eacute; chợ Cốc Lếu</strong>&nbsp;mua sắm h&agrave;ng&nbsp;<strong>Trung Quốc</strong>. Tham quan chụp ảnh cửa khẩu H&agrave; khẩu<strong>&nbsp;Việt Nam &ndash; Trung Quốc.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/19/11/ivivu-cho-coc-leu-lao-cai.gif\" /></strong></p>\r\n\r\n<p><em>Chợ Cốc Lếu.</em></p>\r\n\r\n<p>Sau đ&oacute; Qu&yacute; kh&aacute;ch khởi h&agrave;nh tr&ecirc;n&nbsp;<strong>Cao Tốc L&agrave;o Cai &ndash; H&agrave; Nội</strong>&nbsp;trở về s&acirc;n bay&nbsp;<strong>Nội B&agrave;i</strong>&nbsp;khởi h&agrave;nh về&nbsp;<strong>S&agrave;i G&ograve;n</strong>&nbsp;tr&ecirc;n chuyến bay VJ155 19:20 - 21:20.</p>', '<p>- Du kh&aacute;ch mang theo giấy CMND hoặc Hộ chiếu (Bản ch&iacute;nh), n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ, kh&ocirc;ng mang valy lớn.</p>\r\n\r\n<p>- Trẻ em phải đi c&ugrave;ng cha mẹ hoặc c&oacute; giấy cam kết đi tour c&ugrave;ng người th&acirc;n, khi đi phải mang theo giấy khai sinh hoặc hộ chiếu.</p>\r\n\r\n<p>- Du kh&aacute;ch tự quản l&yacute; tiền bạc tư trang trong qua tr&igrave;nh tham quan du lịch.</p>\r\n\r\n<p>- Khi đăng k&yacute;, qu&yacute; kh&aacute;ch vui l&ograve;ng cung cấp đầy đủ Họ t&ecirc;n v&agrave; năm sinh.</p>\r\n\r\n<p>- Đối với du kh&aacute;ch l&agrave; Việt kiều, Quốc tế nhập cảnh Việt Nam bằng visa rời, vui l&ograve;ng mang theo tờ khai hải quan v&agrave; visa khi đi tour.</p>\r\n\r\n<p>- Thứ tự c&aacute;c điểm tham quan trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế. Nhưng kh&ocirc;ng hủy bỏ hoặc thay đổi m&agrave; kh&ocirc;ng c&oacute; sự thỏa thuận với Đo&agrave;n.</p>\r\n\r\n<p>- Phụ thu ph&ograve;ng đơn: Trường hợp kh&aacute;ch lẻ đi 1 m&agrave; ko ngủ gh&eacute;p đo&agrave;n được hoặc kh&aacute;ch muốn ở ri&ecirc;ng 1 ph&ograve;ng.</p>\r\n\r\n<p>*Trong những trường hợp kh&aacute;ch quan như: khủng bố, thi&ecirc;n tai&hellip;hoặc do c&oacute; sự cố, c&oacute; sự thay đổi lịch tr&igrave;nh của c&aacute;c phương tiện vận chuyển c&ocirc;ng cộng như: m&aacute;y bay, t&agrave;u hỏa&hellip;th&igrave; Cty sẽ giữ quyền thay đổi lộ tr&igrave;nh bất cứ l&uacute;c n&agrave;o v&igrave; sự thuận tiện, an to&agrave;n cho kh&aacute;ch h&agrave;ng v&agrave; sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm bồi thường những thiệt hại ph&aacute;t sinh**.</p>', 4, '19:54:00', '2020-11-22', '2020-11-24', 'Ô Tô', '2020-Nov-Sat_1605945373187.gif', 0, 0, 0, '2', '2', '2020-11-21 00:56:13', '2020-11-21 00:56:13'),
(8, 'faIvCuDvxz', 15, 'Tour Đà Nẵng 3N2D: Sơn Trà - Bà Nà - Ngũ Hành Sơn - Hội An (Du Xuân)', 'Sơn Trà', 'Hội An', 2, 3490000, 2490000, 1490000, '<h3>Vui th&uacute; Đ&agrave; th&agrave;nh mẩn m&ecirc; qu&ecirc;n lối về</h3>\r\n\r\n<p>Đ&agrave; Nẵng l&agrave; một trong 20 th&agrave;nh phố sạch nhất thế giới. Nằm ở v&ugrave;ng Nam Trung Bộ, Đ&agrave; Nẵng c&oacute; cả n&uacute;i, đồng bằng v&agrave; biển. C&aacute;c điểm tham quan du lịch nổi tiếng khi du lịch Đ&agrave; Nẵng bao gồm khu du lịch B&agrave; N&agrave;, b&atilde;i biển Mỹ Kh&ecirc;, v&agrave; khu vui chơi giải tr&iacute; trong nh&agrave; Fantasy Park lớn nhất ch&acirc;u &Aacute;. Đ&agrave; Nẵng c&ograve;n c&oacute; nhiều thắng cảnh m&ecirc; hồn như đ&egrave;o Hải V&acirc;n, rừng nguy&ecirc;n sinh ở b&aacute;n đảo Sơn Tr&agrave; v&agrave; Ngũ H&agrave;nh Sơn. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; Đ&agrave; Nẵng theo c&aacute;ch ri&ecirc;ng của m&igrave;nh ngay h&ocirc;m nay!!!</p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;đi qua những điểm tham quan g&acirc;y thương nhớ cho du kh&aacute;ch:&nbsp;</p>\r\n\r\n<p>- Thưởng ngoạn to&agrave;n cảnh phố biển&nbsp;<strong>Đ&agrave; Nẵng</strong>&hellip;</p>\r\n\r\n<p>- Tham quan B&aacute;n<strong>&nbsp;Đảo Sơn Tr&agrave;, Viếng Linh Ứng Tự</strong></p>\r\n\r\n<p>- Tản bộ ngắm phố cổ&nbsp;<strong>Hội An</strong>.</p>\r\n\r\n<p>- Tham quan l&agrave;ng đi&ecirc;u khắc,&nbsp;<strong>Ngũ H&agrave;nh Sơn</strong>.</p>', '<h3>NG&Agrave;Y&nbsp;01: HỒ CH&Iacute; MINH&nbsp;- Đ&Agrave; NẴNG - SƠN TR&Agrave;&nbsp;- HỘI AN ( ĂN TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng 05:00: Hướng dẫn vi&ecirc;n đ&oacute;n v&agrave; l&agrave;m thủ tục đ&aacute;p chuyến bay đi Đ&agrave; Nẵng tại&nbsp;<strong>ga quốc nội s&acirc;n bay T&acirc;n Sơn Nhất.</strong></p>\r\n\r\n<p>08:00: Xe &amp; Hướng Dẫn Vi&ecirc;n đ&oacute;n đo&agrave;n tại s&acirc;n bay Đ&agrave; Nẵng. Khởi h&agrave;nh đi&nbsp;<strong>B&aacute;n Đảo Sơn Tr&agrave; (Monkey Mountain)</strong>&nbsp;v&ograve;ng quanh B&aacute;n Đảo để thưởng ngoạn to&agrave;n cảnh phố biển Đ&agrave; Nẵng tr&ecirc;n cao. Xe đưa qu&yacute; kh&aacute;ch dọc theo triền n&uacute;i Đ&ocirc;ng Nam để chi&ecirc;m ngưỡng vẻ đẹp tuyệt mỹ của biển Đ&agrave; Nẵng, viếng&nbsp;<strong>Linh Ứng Tự</strong>&nbsp;- nơi c&oacute; tượng Phật B&agrave; 65m cao nhất Việt Nam.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-chua-linh-ung.jpg\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Linh Ứng.</em></p>\r\n\r\n<p>Trưa: Xe đưa đo&agrave;n đi d&ugrave;ng bữa trưa với đặc sản nổi tiếng Đ&agrave; Nẵng &ldquo;B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; Mỳ Quảng&rdquo;. Xe đưa Đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi. &nbsp;&nbsp;</p>\r\n\r\n<p>Tiếp tục: Xe đưa đo&agrave;n v&agrave;o&nbsp;<strong>Phố cổ Hội An</strong>&nbsp;(Tỉnh Quảng Nam), Qu&yacute; kh&aacute;ch tiếp tục h&agrave;nh tr&igrave;nh tham quan Phố cổ với c&aacute;c di t&iacute;ch cổ k&iacute;nh như:&nbsp;<strong>ch&ugrave;a cầu Nhật Bản, hội qu&aacute;n Phước Kiến, nh&agrave; cổ Ph&ugrave;ng Hưng.</strong>&nbsp;Sau bữa cơm chiều, đo&agrave;n tự do dạo phố thưởng ngoạn Hội An về đ&ecirc;m lung linh trong mu&ocirc;n sắc đ&egrave;n lồng, tham gia nhiều chương tr&igrave;nh nghệ thuật d&acirc;n gian truyền thống v&agrave; thưởng thức những m&oacute;n đặc sản d&acirc;n d&atilde; đậm chất địa phương.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-chua-cau-hoi-an.jpg\" /></p>\r\n\r\n<p><em>Phổ Cổ Hội An.</em></p>\r\n\r\n<p>Tối: Ăn tối nh&agrave; h&agrave;ng. Qu&yacute; kh&aacute;ch ngồi thuyền tr&ecirc;n s&ocirc;ng H&agrave;n (Chi ph&iacute; tự t&uacute;c) ngắm th&agrave;nh phố Đ&agrave; Nẵng lung linh về đ&ecirc;m với h&agrave;ng loạt c&acirc;y Cầu độc đ&aacute;o v&agrave; nổi tiếng hoặc xem cầu Rồng phun Lửa v&agrave; Nước (Thứ bảy hoặc chủ nhật h&agrave;ng tuần).</p>\r\n\r\n<h3>NG&Agrave;Y&nbsp;02:&nbsp;KH&Aacute;M PH&Aacute; Đ&Agrave; NẴNG&nbsp;( ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>S&aacute;ng: Sau bữa điểm t&acirc;m, Qu&yacute; kh&aacute;ch đi tham quan L&agrave;ng đi&ecirc;u khắc đ&aacute;&nbsp;<strong>Non Nước</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/27/09/ivivu-lang-da-my-nghe-non-nuoc.gif\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>L&agrave;ng Đi&ecirc;u Khắc Đ&agrave; Non Nước.</em></p>\r\n\r\n<p>Sau đ&oacute; di chuyển qua&nbsp;<strong>Ngũ H&agrave;nh Sơn</strong>, Qu&yacute; kh&aacute;ch chi&ecirc;m b&aacute;i c&aacute;c danh lam v&agrave; thưởng ngoạn c&aacute;c thắng cảnh nổi tiếng nơi đ&acirc;y như ch&ugrave;a Linh Ứng, Tam Thai, Non Nước; c&aacute;c động như: T&agrave;ng Chơn, Huyền Kh&ocirc;ng, Hoa Nghi&ecirc;m; đứng tr&ecirc;n Vọng Giang Đ&agrave;i hoặc Vọng Hải Đ&agrave;i ngắm nh&igrave;n to&agrave;n cảnh n&uacute;i s&ocirc;ng tr&ugrave;ng điệp v&agrave; biển cả bao la...</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/12/26/11/ivivu-ngu-hanh-son.gif\" /></p>\r\n\r\n<p><em>Ngũ H&agrave;nh Sơn.</em></p>\r\n\r\n<p>Tiếp Tục: Qu&yacute; kh&aacute;ch c&oacute; thể tham gia th&ecirc;m tour mở</p>\r\n\r\n<p><strong>LỰA CHỌN 1</strong>: Khởi h&agrave;nh v&agrave;o Hội An ngồi t&agrave;u Cao Tốc tại biển Cửa Đại đi tham quan&nbsp;<strong>C&ugrave; Lao Ch&agrave;m</strong>&nbsp;(Tự t&uacute;c chi ph&iacute;, gi&aacute; tham khảo 550.000đ/kh&aacute;ch), khu dự trữ sinh quyển Thế giới gồm cụm đảo ngo&agrave;i khơi biển Đ&ocirc;ng c&aacute;ch Hội An 12 hải l&yacute;. Tham quan ch&ugrave;a Hải Tạng hơn 400 năm tuổi, &Acirc;u thuyền &ndash; khu vực tr&aacute;nh b&atilde;o của ngư d&acirc;n, Khu d&acirc;n cư &amp; c&aacute;c lăng miếu cổ. (Nếu Q&uacute;y kh&aacute;ch đi v&agrave;o những ng&agrave;y c&oacute; mưa, b&atilde;o, C&ocirc;ng ty sẽ thay thế bằng những option b&ecirc;n dưới)</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2020/05/08/09/ivivu-cu-lao-cham2.gif\" /></p>\r\n\r\n<p><em>C&ugrave; Lao Ch&agrave;m.</em></p>\r\n\r\n<p><strong>LỰA CHỌN 2</strong>: Q&uacute;y kh&aacute;ch khởi h&agrave;nh đến C&ocirc;ng vi&ecirc;n Suối kho&aacute;ng n&oacute;ng&nbsp;<strong>N&uacute;i Thần T&agrave;i</strong>&nbsp;để kh&aacute;m ph&aacute; những lợi &iacute;ch của suối kho&aacute;ng n&oacute;ng, thư gi&atilde;n cơ thể sau những thời gian căng thẳng v&agrave; mệt mỏi (Tự t&uacute;c chi ph&iacute;, gi&aacute; tham khảo 550.000đ/ kh&aacute;ch)</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/11/26/09/ivivu-nui-than-tai.jpg\" /></p>\r\n\r\n<p><em>N&uacute;i Thần T&agrave;i.</em></p>\r\n\r\n<p>Nếu Qu&yacute; kh&aacute;ch kh&ocirc;ng tham gia tour mở, th&igrave; Qu&yacute; Kh&aacute;ch c&oacute; thể tự do tại kh&aacute;ch sạn v&agrave; tắm biển/hồ bơi.</p>\r\n\r\n<p>Trưa: Đo&agrave;n d&ugrave;ng bữa trưa v&agrave; tự do tắm biển, hồ bơi kh&aacute;ch sạn.</p>\r\n\r\n<p>Chiều: Xe đ&oacute;n Đo&agrave;n tham quan 1 v&ograve;ng th&agrave;nh phố&nbsp;<strong>Đ&agrave; Nẵng</strong>.</p>\r\n\r\n<p>Tối: Q&uacute;y kh&aacute;ch d&ugrave;ng bữa tối tại Nh&agrave; h&agrave;ng địa phương.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể tự do dạo quanh th&agrave;nh phố&nbsp;<strong>Đ&agrave; Nẵng</strong>, chi&ecirc;m ngưỡng vẻ đẹp&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;từ tr&ecirc;n cao khi tham quan V&ograve;ng Quay Mặt Trời (Chi ph&iacute; tự t&uacute;c).</p>\r\n\r\n<h3>NG&Agrave;Y&nbsp;03: Đ&Agrave; NẴNG&nbsp;- B&Agrave; N&Agrave;&nbsp;- HỒ CH&Iacute; MINH ( ĂN S&Aacute;NG)</h3>\r\n\r\n<p>S&aacute;ng: D&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn tự do tắm biển tại b&atilde;i biển Mỹ Kh&ecirc; v&agrave; mua sắm đến 10:00 Q&uacute;y Kh&aacute;ch c&oacute; thể tham gia tour mở l&ecirc;n&nbsp;<strong>B&agrave; N&agrave;</strong>, thi&ecirc;n đường nghỉ dưỡng miền Trung với nhiều thắng cảnh, dịch vụ vui chơi v&agrave; giải tr&iacute; hấp dẫn (Tự t&uacute;c chi ph&iacute; v&eacute; 860.000đ bao gồm v&eacute; C&aacute;p Treo B&agrave; N&agrave; v&agrave; bữa ăn trưa dưới ch&acirc;n n&uacute;i). Nơi m&agrave; qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; những khoảnh khắc giao m&ugrave;a bất ngờ Xu&acirc;n &ndash; Hạ - Thu &ndash; Đ&ocirc;ng trong 1 ng&agrave;y.&nbsp; Ngồi c&aacute;p treo d&agrave;i nhất thế giới (gần 6.000m), tham quan&nbsp;<strong>Đồi Vọng Nguyệt, ch&ugrave;a Linh Ứng, Th&iacute;ch Ca Phật Đ&agrave;i, c&acirc;y Cầu V&agrave;ng B&agrave; N&agrave;</strong>&nbsp;thu h&uacute;t h&agrave;ng triệu lượt du kh&aacute;ch đến đ&acirc;y tham quan, khu chuồng ngựa cũ của Ph&aacute;p, vườn tịnh t&acirc;m v&agrave; đỉnh nh&agrave; r&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/12/06/17/ivivu-cau-vang-ba-na.jpg\" /></p>\r\n\r\n<p><em>Cầu V&agrave;ng B&agrave; N&agrave; Hills.</em></p>\r\n\r\n<p>Trưa: Q&uacute;y kh&aacute;ch tự t&uacute;c d&ugrave;ng bữa trưa với đặc sản Đ&agrave; Nẵng. Q&uacute;y kh&aacute;ch l&agrave;m thủ tục trả ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Chiều: 14:30 Hướng dẫn vi&ecirc;n tiễn kh&aacute;ch s&acirc;n bay Đ&agrave; Nẵng đ&aacute;p chuyến bay về lại TP. Hồ Ch&iacute; Minh. Kết th&uacute;c chương tr&igrave;nh, chia tay v&agrave; hẹn gặp lại Qu&yacute; Kh&aacute;ch trong c&aacute;c chuyến trong trong v&agrave; ngo&agrave;i nước.</p>', '<p>- Trẻ em từ 02 tuổi đến dưới 05 tuổi: gi&aacute; tour theo khung gi&aacute; b&ecirc;n tr&ecirc;n (02 vợ chồng được k&egrave;m theo 1 trẻ, trẻ Em thứ 2 cần mua theo xuất người lớn để đảm bảo chỗ ngủ ph&ugrave; hợp). Ti&ecirc;u chuẩn trẻ em c&oacute; v&eacute; m&aacute;y bay, ghế ngồi tr&ecirc;n xe v&agrave; ngủ chung với Cha Mẹ. C&aacute;c chi ph&iacute; ăn uống tham quan c&oacute; ph&aacute;t sinh gia đ&igrave;nh tự lo cho Em.</p>\r\n\r\n<p>- Trẻ em từ 05 tuổi đến dưới 11 tuổi: gi&aacute; tour theo khung gi&aacute; b&ecirc;n tr&ecirc;n (02 vợ chồng được k&egrave;m theo 1 trẻ, trẻ Em thứ 2 cần mua theo xuất người lớn để đảm bảo chỗ ngủ ph&ugrave; hợp). Ti&ecirc;u chuẩn trẻ em c&oacute; v&eacute; m&aacute;y bay, ghế ngồi tr&ecirc;n xe v&agrave; ngủ chung với Cha Mẹ.</p>\r\n\r\n<p>- Em b&eacute; dưới 02 tuổi: 500.000 VNĐ/B&Eacute; bao gồm c&oacute; thuế ph&iacute; h&agrave;ng kh&ocirc;ng, bảo hiểm (Kh&ocirc;ng bao gồm ghế ngồi tr&ecirc;n xe, Phụ thu th&ecirc;m 300.000 VNĐ nếu mua th&ecirc;m suất ghế ngồi, nhưng phải đăng k&yacute; ngay khi đăng k&yacute; tour) nhưng 2 vợ chồng chỉ được k&egrave;m 01 em b&eacute; dưới 02 tuổi, nếu b&eacute; thứ 02 t&iacute;nh gi&aacute; trẻ em theo khung từ 2 đến dưới 5 tuổi.</p>\r\n\r\n<p>- Từ 11 tuổi trở l&ecirc;n được xem như người lớn.</p>', 3, '18:57:00', '2020-11-21', '2020-11-23', 'Ô tô', '2020-Nov-Sat_1605945540119.gif', 10, 0, 0, '2', '2', '2020-11-21 00:59:00', '2020-11-21 00:59:00'),
(9, 'jUO28zfaiG', 20, 'Tour Đà Nẵng 4N3D: Sơn Trà - Hội An - Bà Nà (Cầu Vàng) - Huế', 'Sơn Trà', 'Hội An - Bà Nà (Cầu Vàng) - Huế', 10, 2550000, 1550000, 1000000, '<h3>Chinh phục cung đường di sản miền Trung</h3>\r\n\r\n<p>Miền Trung, dải đất th&acirc;n thương nối liền hai miền Nam - Bắc của Việt Nam. &ldquo;Cung đường di sản miền Trung&rdquo; sẽ kết nối du kh&aacute;ch với c&aacute;c di sản văn h&oacute;a của nh&acirc;n loại được UNESCO c&ocirc;ng nhận bao gồm: Huế với hai di sản l&agrave; Quần thể di t&iacute;ch Cố đ&ocirc; Huế v&agrave; Nh&atilde; Nhạc cung đ&igrave;nh; Quảng Nam với hai di sản l&agrave; Th&aacute;nh địa Mỹ Sơn v&agrave; đ&ocirc; thị cổ Hội An. Tr&ecirc;n mỗi bước đi, du kh&aacute;ch c&ograve;n c&oacute; thể kh&aacute;m ph&aacute; cho m&igrave;nh nhiều điểm đến hấp dẫn: B&agrave; N&agrave; Hill, L&agrave;ng Ch&agrave;i Lăng C&ocirc;, v&agrave; nhiều b&atilde;i tắm tuyệt đẹp: Cửa Đại, Non Nước, Lăng C&ocirc;, Nhật Lệ, c&ugrave;ng với những n&eacute;t ẩm thực độc đ&aacute;o của Miền Trung. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; cung đường tuyệt vời n&agrave;y ngay h&ocirc;m nay!</p>\r\n\r\n<p><em><strong>Đặt đo&agrave;n ri&ecirc;ng li&ecirc;n hệ 028 3933 8002 hoặc hotline 0906 355 542 để c&oacute; gi&aacute; tốt 😍</strong></em></p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Du kh&aacute;ch sẽ kh&ocirc;ng ngừng thổn thức bởi...</p>\r\n\r\n<p>-&nbsp;Hội An&nbsp;với&nbsp;Ch&ugrave;a Cầu Nhật Bản,&nbsp;Nh&agrave; Cổ&nbsp;h&agrave;ng trăm năm tuổi,&nbsp;Hội Qu&aacute;n Phước Kiến&nbsp;&amp; Xưởng thủ c&ocirc;ng mỹ nghệ.</p>\r\n\r\n<p>- Lưu lại những bức ảnh để đời ở&nbsp;cầu B&agrave;n Tay V&agrave;ng&nbsp;tr&ecirc;n&nbsp;B&agrave; N&agrave; Hills</p>\r\n\r\n<p>- Thưởng thức&nbsp;Ca Huế tr&ecirc;n S&ocirc;ng Hương</p>\r\n\r\n<p>- Kh&aacute;m ph&aacute;&nbsp;Đại Nội Huế&nbsp;với những kiến tr&uacute;c độc đ&aacute;o c&ograve;n lại thời nh&agrave; Nguyễn.</p>', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>KHUYẾN M&Atilde;I &Aacute;P DỤNG TỪ&nbsp;16/05/2020 &ndash; 15/07/2020.</h3>\r\n\r\n<p>- Tặng v&eacute; Du thuyền S&ocirc;ng H&agrave;n với c&aacute;c dịch vụ ca m&uacute;a nhạc hấp dẫn.</p>\r\n\r\n<p>- Tặng v&eacute; Buffet trưa tại nh&agrave; h&agrave;ng 4 sao Banahills với menu ẩm thực 3 miền hấp dẫn v&agrave; phong ph&uacute;.</p>\r\n\r\n<h3>NG&Agrave;Y 01: Đ&Oacute;N KH&Aacute;CH &ndash; SƠN TR&Agrave; - Đ&Agrave; NẴNG (ĂN TRƯA, TỐI)</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;(S&acirc;n bay, Ga, điểm hẹn trung t&acirc;m) từ 07h00 đến 13h00 (Sau thời gian n&agrave;y, qu&yacute; kh&aacute;ch tự t&uacute;c nhập đo&agrave;n). Xe đưa đi ăn trưa đặc sản nổi tiếng Đ&agrave; Nẵng &ldquo;<strong>B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; Mỳ Quảng</strong>&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/08/23/15/ivivu-banh-trang-thit-heo-2-dau-da.jpg\" style=\"height:398px; width:710px\" /></p>\r\n\r\n<p>&nbsp;<em>B&aacute;nh Tr&aacute;ng Thịt Heo.</em></p>\r\n\r\n<p>Nhận ph&ograve;ng K/sạn 3* nghỉ ngơi.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp; Khởi h&agrave;nh đến&nbsp;<strong>B&aacute;n Đảo Sơn Tr&agrave;</strong>&nbsp;&ndash; Rừng gi&agrave; giữa l&ograve;ng phố trẻ viếng&nbsp;<strong>Linh Ứng Tự</strong>&nbsp;- nơi c&oacute; tượng Phật B&agrave; 67m hướng ra biển Đ&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-chua-linh-ung.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Linh Ứng Sơn Tr&agrave;.</em></p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>Sau đ&oacute; qu&yacute; kh&aacute;ch tự t&uacute;c trải nghiệm h&agrave;ng loạt tr&ograve; chơi hiện đại, cảm gi&aacute;c mạnh h&agrave;ng đầu&nbsp;<strong>Đ&ocirc;ng Nam &Aacute;</strong>&nbsp;tại&nbsp;<strong>Sunworld Danang Wonders - C&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute;</strong>&nbsp;(chi ph&iacute; tự t&uacute;c).</p>\r\n\r\n<h3>NG&Agrave;Y 02:&nbsp;BẢO T&Agrave;NG PHẬT HỌC &ndash; PHỐ CỔ HỘI AN (ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. V&ograve;ng qua&nbsp;<strong>Cầu Rồng</strong>&nbsp;lưu lại dấu ấn tr&ecirc;n&nbsp;<strong>Cầu T&igrave;nh Y&ecirc;u</strong>, tản bộ thưởng thức kh&ocirc;ng kh&iacute; trong l&agrave;nh b&ecirc;n bờ H&agrave;n Giang với tượng&nbsp;<strong>C&aacute; Ch&eacute;p H&oacute;a Long</strong>&nbsp;- Biểu tượng mong muốn vươn l&ecirc;n của người Đ&agrave; Nẵng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/20/10/ivivu-cau-rong.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Cầu Rồng.</em></p>\r\n\r\n<p>Tham quan&nbsp;<strong>Ch&ugrave;a Quan Thế &Acirc;m</strong>&nbsp;-&nbsp;<strong>&nbsp;Bảo T&agrave;ng Phật Học</strong>&nbsp;v&agrave;&nbsp;<strong>L&agrave;ng Nghề Đi&ecirc;u Khắc Đ&aacute;</strong>. Mua sắm đặc sản Qu&agrave; Miền Trung.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp; Khởi h&agrave;nh v&agrave;o&nbsp;<strong>Hội An</strong>&nbsp;b&aacute;ch bộ tham quan v&agrave; mua sắm Phố Cổ với:&nbsp;<strong>Ch&ugrave;a Cầu Nhật Bản, Nh&agrave; Cổ h&agrave;ng trăm năm tuổi, Hội Qu&aacute;n Phước Kiến</strong>&nbsp;&amp; Xưởng thủ c&ocirc;ng mỹ nghệ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/06/22/14/ivivu-pho-co-hoi-an.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p>&nbsp;<em>Hội An.</em></p>\r\n\r\n<p>Khởi h&agrave;nh về lại&nbsp;<strong>Đ&agrave; Nẵng.</strong></p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng. Tự do thưởng ngoạn du thuyền tr&ecirc;n s&ocirc;ng H&agrave;n ngắm cảnh Đ&agrave; Th&agrave;nh về đ&ecirc;m, chụp ảnh&nbsp;<strong>Cầu Quay S&ocirc;ng H&agrave;n</strong>, cầu&nbsp;<strong>Rồng phun lửa</strong>&nbsp;v&agrave; nước v&agrave;o cuối tuần.</p>\r\n\r\n<h3>NG&Agrave;Y 03: B&Agrave; N&Agrave; - CỐ Đ&Ocirc; HUẾ (ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>Điểm t&acirc;m. Khởi h&agrave;nh đi&nbsp;<strong>B&agrave; N&agrave; - N&uacute;i Ch&uacute;</strong>a.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch sẽ tận hưởng cảm gi&aacute;c bồng bền khi ngồi tr&ecirc;n Cabin lơ lững giữa chừng m&acirc;y.&nbsp;<strong>Viếng ch&ugrave;a Linh Ứng</strong>&nbsp;với tượng Phật Th&iacute;ch Ca cao 27m, viếng đền thờ&nbsp;<strong>B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n.</strong>&nbsp;Tham quan&nbsp;<strong>Cầu v&agrave;ng - HOT</strong>&nbsp;C&acirc;y cầu độc đ&aacute;o nằm trong vườn Thi&ecirc;n Thai ở B&agrave; N&agrave; Hill - được x&acirc;y dựng tr&ecirc;n độ cao 1.400m so với mặt nước biển, c&acirc;y cầu gồm 8 nhịp v&agrave; c&oacute; độ d&agrave;i l&agrave; 150m. C&acirc;y cầu được n&acirc;ng đỡ bởi đ&ocirc;i b&agrave;n tay khổng lồ. Người đi như lạc giữa ti&ecirc;n cảnh nơi c&oacute; m&acirc;y, trời bao la. (V&eacute; c&aacute;p treo B&agrave; N&agrave; tự t&uacute;c)</p>\r\n\r\n<p>Tham gia vui chơi tại&nbsp;<strong>c&ocirc;ng vi&ecirc;n Fantasy Park</strong>: với c&aacute;c tr&ograve; chơi phi&ecirc;u lưu mới lạ như: V&ograve;ng Quay T&igrave;nh Y&ecirc;u, Phi C&ocirc;ng Skiver, Đường Đua Lửa, Ng&ocirc;i Nh&agrave; Ma v&agrave; Khu trưng b&agrave;y hơn 40 tượng s&aacute;p những nh&acirc;n vật nổi tiếng tr&ecirc;n thế giới&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/06/22/14/ivivu-ba-na-nui-chua.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>B&agrave; N&agrave; Hills.</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Khởi h&agrave;nh đi&nbsp;<strong>Cố Đ&ocirc; Huế - Di sản văn ho&aacute; Thế Giới</strong>, xuy&ecirc;n hầm đường bộ đ&egrave;o Hải v&acirc;n, dừng ch&acirc;n chụp ảnh l&agrave;ng&nbsp;<strong>Ch&agrave;i Lăng C&ocirc;.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/12/06/17/ivivu-lang-chai-lang-co.jpg\" style=\"height:390px; width:710px\" /></strong></p>\r\n\r\n<p><em>L&agrave;ng Ch&agrave;i Lăng C&ocirc;.</em></p>\r\n\r\n<p>Nhận ph&ograve;ng K/sạn 4* nghỉ ngơi.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng với đặc sản xứ Huế (B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i,...). Ngồi thuyền Rồng nghe&nbsp;<strong>Ca Huế</strong>&nbsp;v&agrave; thả hoa đăng cầu may tr&ecirc;n d&ograve;ng Hương Giang thơ mộng.</p>\r\n\r\n<h3>NG&Agrave;Y 04:&nbsp;LĂNG KHẢI ĐỊNH - ĐẠI NỘI &ndash; CH&Ugrave;A THI&Ecirc;N MỤ - TIỄN KH&Aacute;CH (ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. Điểm t&acirc;m. Khởi h&agrave;nh tham quan tham quan&nbsp;<strong>Đại Nội - Ho&agrave;ng Cung</strong>&nbsp;của 13 vị vua triều Nguyễn, triều đại phong kiến cuối c&ugrave;ng của Việt Nam v&agrave;&nbsp;<strong>Ch&ugrave;a Thi&ecirc;n Mụ</strong>&nbsp;cổ k&iacute;nh. Đo&agrave;n tiếp tục di chuyển đến tham quan&nbsp;<strong>Lăng Khải Định</strong>&nbsp;&ndash; c&ocirc;ng tr&igrave;nh lăng c&oacute; được sự kết hợp tuyệt vời của kiến tr&uacute;c Đ&ocirc;ng - T&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/05/14/17/ivivu-dai-noi-hue.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Đại Nội Ho&agrave;ng Cung.</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng tại<strong>&nbsp;Huế.</strong></p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Tiễn Qu&yacute; kh&aacute;ch tại&nbsp;<strong>s&acirc;n bay Huế</strong>&nbsp;(c&aacute;c chuyến bay sau 17:00).</p>\r\n\r\n<p>Khởi h&agrave;nh về Đ&agrave; Nẵng, tắm biển&nbsp;<strong>Mỹ Kh&ecirc;</strong>. Tiễn qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng (c&aacute;c chuyến bay sau 19:00).</p>\r\n\r\n<p>Kết th&uacute;c chương tr&igrave;nh. Hẹn gặp lại Qu&yacute; kh&aacute;ch!</p>', '<p>-&nbsp;Tr&igrave;nh tự chương tr&igrave;nh c&oacute; thể thay đổi nhưng số lượng chương tr&igrave;nh vẫn kh&ocirc;ng thay đổi.</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch c&oacute; mặt tại điểm đ&oacute;n trước 15 ph&uacute;t. Du kh&aacute;ch đến trễ khi xe đ&atilde; khởi h&agrave;nh hoặc hủy tour kh&ocirc;ng b&aacute;o trước vui l&ograve;ng chịu ph&iacute; như &lsquo;hủy v&eacute; ngay ng&agrave;y khởi h&agrave;nh&rsquo;</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch mang theo giấy CMND hoặc Hộ chiếu (Bản ch&iacute;nh), n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ, kh&ocirc;ng mang valy lớn.</p>\r\n\r\n<p>-&nbsp;Trẻ em phải đi c&ugrave;ng cha mẹ hoặc c&oacute; giấy cam kết đi tour c&ugrave;ng người th&acirc;n, khi đi phải mang theo giấy khai sinh hoặc hộ chiếu.</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch tự quản l&yacute; tiền bạc tư trang trong qua tr&igrave;nh tham quan du lịch.</p>\r\n\r\n<p>-&nbsp;Khi đăng k&yacute;, qu&yacute; kh&aacute;ch vui l&ograve;ng cung cấp đầy đủ Họ t&ecirc;n v&agrave; năm sinh.</p>\r\n\r\n<p>-&nbsp;Đối với du kh&aacute;ch l&agrave; Việt kiều, Quốc tế nhập cảnh Việt Nam bằng visa rời, vui l&ograve;ng mang theo tờ khai hải quan v&agrave; visa khi đi tour.</p>\r\n\r\n<p>-&nbsp;Chương tr&igrave;nh tour được coi l&agrave; bi&ecirc;n bản ghi nhớ giữa kh&aacute;ch h&agrave;ng v&agrave; Ivivu. Những thắc mắc, khiếu nại sẽ dựa v&agrave;o những quy định trong chương tr&igrave;nh v&agrave; phiếu thu để giải quyết.</p>', 4, '18:00:00', '2020-11-22', '2020-11-25', 'Ô tô', '2020-Nov-Sat_1605945744949.jpg', 15, 0, 0, '2', '2', '2020-11-21 01:02:24', '2020-11-21 01:02:24');
INSERT INTO `tours` (`id`, `tour_code`, `count_people`, `tour_name`, `departure_location`, `destination`, `province_id`, `price_adult`, `price_kid`, `single_room_price`, `tour_detail`, `tour_program`, `tour_note`, `number_of_day`, `departure_time`, `departure_date`, `return_date`, `vehicle`, `tour_image`, `discount`, `status`, `isdeleted`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
(10, 'cTYbXOuovU', 25, 'Tour Đà Nẵng 4N3D: Hội An - Bà Nà - Huế', 'Hội An', 'Huế', 10, 3050000, 1050000, 1000000, '<h3>Chinh phục cung đường di sản miền Trung</h3>\r\n\r\n<p>Miền Trung, dải đất th&acirc;n thương nối liền hai miền Nam - Bắc của Việt Nam. &ldquo;Cung đường di sản miền Trung&rdquo; sẽ kết nối du kh&aacute;ch với c&aacute;c di sản văn h&oacute;a của nh&acirc;n loại được UNESCO c&ocirc;ng nhận bao gồm: Quảng B&igrave;nh c&ugrave;ng Vườn quốc gia Phong Nha - Kẻ B&agrave;ng; Huế với hai di sản l&agrave; Quần thể di t&iacute;ch Cố đ&ocirc; Huế v&agrave; Nh&atilde; Nhạc cung đ&igrave;nh; Quảng Nam với hai di sản l&agrave; Th&aacute;nh địa Mỹ Sơn v&agrave; đ&ocirc; thị cổ Hội An. Tr&ecirc;n mỗi bước đi, du kh&aacute;ch c&ograve;n c&oacute; thể kh&aacute;m ph&aacute; cho m&igrave;nh nhiều điểm đến hấp dẫn: B&agrave; N&agrave; Hill, L&agrave;ng Ch&agrave;i Lăng C&ocirc;, v&agrave; nhiều b&atilde;i tắm tuyệt đẹp: Cửa Đại, Non Nước, Lăng C&ocirc;, Nhật Lệ, c&ugrave;ng với những n&eacute;t ẩm thực độc đ&aacute;o của Miền Trung. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; cung đường tuyệt vời n&agrave;y ngay h&ocirc;m nay!<br />\r\n<em><strong>Đặt đo&agrave;n ri&ecirc;ng li&ecirc;n hệ 028 3933 8002 hoặc hotline 0906 355 542 để c&oacute; gi&aacute; tốt 😍</strong></em></p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Du kh&aacute;ch sẽ kh&ocirc;ng ngừng thổn thức bởi...</p>\r\n\r\n<p>-&nbsp;<strong>Hội An</strong>&nbsp;với&nbsp;<strong>Ch&ugrave;a Cầu Nhật Bản,&nbsp;Nh&agrave; Cổ&nbsp;h&agrave;ng trăm năm tuổi,&nbsp;Hội Qu&aacute;n Phước Kiến</strong>&nbsp;&amp; Xưởng thủ c&ocirc;ng mỹ nghệ.</p>\r\n\r\n<p>- Lưu lại những bức ảnh để đời ở&nbsp;<strong>cầu B&agrave;n Tay V&agrave;ng</strong>&nbsp;tr&ecirc;n&nbsp;<strong>B&agrave; N&agrave; Hills</strong></p>\r\n\r\n<p>- Thưởng thức&nbsp;<strong>Ca Huế tr&ecirc;n S&ocirc;ng Hương</strong></p>\r\n\r\n<p>-&nbsp;<strong>Động Thi&ecirc;n Đường:</strong>&nbsp;Ho&agrave;ng cung l&ograve;ng đất sở hữu kỳ quan tr&aacute;ng lệ v&agrave; huyền ảo bậc nhất thế giới.&nbsp;</p>\r\n\r\n<p>- Kh&aacute;m ph&aacute;&nbsp;<strong>Đại Nội Huế</strong>&nbsp;với những kiến tr&uacute;c độc đ&aacute;o c&ograve;n lại thời nh&agrave; Nguyễn.</p>', '<h3>Chương tr&igrave;nh tour</h3>\r\n\r\n<h3>KHUYẾN M&Atilde;I &Aacute;P DỤNG TỪ&nbsp;16/05/2020 &ndash; 15/07/2020.</h3>\r\n\r\n<p>- Tặng v&eacute; Du thuyền S&ocirc;ng H&agrave;n với c&aacute;c dịch vụ ca m&uacute;a nhạc hấp dẫn.</p>\r\n\r\n<p>- Tặng v&eacute; Buffet trưa tại nh&agrave; h&agrave;ng 4 sao Banahills với menu ẩm thực 3 miền hấp dẫn v&agrave; phong ph&uacute;.</p>\r\n\r\n<h3>NG&Agrave;Y 1: BẢO T&Agrave;NG PHẬT HỌC Đ&Agrave; NẴNG &ndash; HỘI AN (ĂN TRƯA, TỐI)&nbsp;</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Đ&oacute;n kh&aacute;ch tại Đ&agrave; Nẵng từ 7h00 &ndash; 09h00 (s&acirc;n bay/ga /điểm hẹn). V&ograve;ng qua&nbsp;<strong>Cầu Rồng</strong>&nbsp;lưu lại dấu ấn tr&ecirc;n Cầu T&igrave;nh Y&ecirc;u, tản bộ thưởng thức kh&ocirc;ng kh&iacute; trong l&agrave;nh b&ecirc;n&nbsp; bờ &nbsp;H&agrave;n Giang với tượng&nbsp;<strong>C&aacute; Ch&eacute;p H&oacute;a Long</strong>&nbsp;- Biểu tượng mong muốn vươn l&ecirc;n của người Đ&agrave; Nẵng. Tham quan&nbsp;<strong>Ch&ugrave;a Quan Thế &Acirc;m - &nbsp;Bảo T&agrave;ng Phật Học v&agrave; L&agrave;ng Nghề Đi&ecirc;u Khắc Đ&aacute;</strong>. Mua sắm đặc sản Q&ugrave;a Miền Trung.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/08/23/15/ivivu-cau-tinh-yeu.jpg\" /></p>\r\n\r\n<p><em>Cầu T&igrave;nh Y&ecirc;u Đ&agrave; Nẵng.</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Khởi h&agrave;nh v&agrave;o Hội An b&aacute;ch bộ tham quan v&agrave; mua sắm&nbsp;<strong>Phố Cổ</strong>&nbsp;với:&nbsp;<strong>Ch&ugrave;a Cầu Nhật Bản</strong>,<strong>&nbsp;Nh&agrave; Cổ</strong>&nbsp;h&agrave;ng trăm năm tuổi,&nbsp;<strong>Hội Qu&aacute;n Phước Kiến &amp; Xưởng thủ c&ocirc;ng mỹ nghệ</strong>. Khởi h&agrave;nh về lại Đ&agrave; Nẵng</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng. Tự do thưởng ngoạn du thuyền tr&ecirc;n&nbsp;<strong>s&ocirc;ng H&agrave;n</strong>&nbsp;ngắm cảnh&nbsp;<strong>Đ&agrave; Th&agrave;nh</strong>&nbsp;về đ&ecirc;m, chụp ảnh&nbsp;<strong>Cầu Quay S&ocirc;ng H&agrave;n</strong>, cầu Rồng phun lửa v&agrave; nước v&agrave;o cuối tuần.</p>\r\n\r\n<h3>NG&Agrave;Y 2: B&Agrave; N&Agrave; (CẦU V&Agrave;NG) - CỐ Đ&Ocirc; HUẾ (ĂN S&Aacute;NG, TRƯA, TỐI) &nbsp; &nbsp; &nbsp;&nbsp;</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. Khởi h&agrave;nh đi&nbsp;<strong>B&agrave; N&agrave; - N&uacute;i Ch&uacute;a</strong>. Qu&yacute; kh&aacute;ch sẽ tận hưởng cảm gi&aacute;c bồng bền khi ngồi tr&ecirc;n Cabin lơ lững giữa chừng m&acirc;y. Viếng&nbsp;<strong>ch&ugrave;a Linh Ứng</strong>&nbsp;với tượng Phật Th&iacute;ch Ca cao 27m, viếng đền thờ&nbsp;<strong>B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n</strong>. Tham quan&nbsp;<strong>Cầu v&agrave;ng - HOT</strong>&nbsp;C&acirc;y cầu độc đ&aacute;o nằm trong vườn Thi&ecirc;n Thai ở&nbsp;<strong>B&agrave; N&agrave; Hill</strong>&nbsp;- được x&acirc;y dựng tr&ecirc;n độ cao 1.400m so với mặt nước biển, c&acirc;y cầu gồm 8 nhịp v&agrave; c&oacute; độ d&agrave;i l&agrave; 150m. C&acirc;y cầu được n&acirc;ng đỡ bởi đ&ocirc;i b&agrave;n tay khổng lồ. Người đi như lạc giữa ti&ecirc;n cảnh nơi c&oacute; m&acirc;y, trời bao la. (V&eacute; c&aacute;p treo B&agrave; N&agrave; tự t&uacute;c).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/12/06/17/ivivu-cau-vang-ba-na.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Cầu B&agrave;n Tay B&agrave; N&agrave; Hills.</em></p>\r\n\r\n<p>- Tham gia vui chơi tại c&ocirc;ng vi&ecirc;n Fantasy Park: với c&aacute;c tr&ograve; chơi phi&ecirc;u lưu mới lạ như: V&ograve;ng Quay T&igrave;nh Y&ecirc;u, Phi C&ocirc;ng Skiver, Đường Đua Lửa, Ng&ocirc;i Nh&agrave; Ma v&agrave; Khu trưng b&agrave;y hơn 40 tượng s&aacute;p những nh&acirc;n vật nổi tiếng tr&ecirc;n thế giới&hellip;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Khởi h&agrave;nh đi&nbsp;<strong>Cố Đ&ocirc; Huế</strong>&nbsp;- Di sản văn ho&aacute; Thế Giới, xuy&ecirc;n hầm đường bộ&nbsp;<strong>đ&egrave;o Hải V&acirc;n</strong>, dừng ch&acirc;n tại&nbsp;<strong>l&agrave;ng ch&agrave;i Lăng C&ocirc;</strong>&nbsp;chụp h&igrave;nh lưu niệm.&nbsp;Nhận ph&ograve;ng kh&aacute;ch sạn 4* nghỉ ngơi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/12/06/17/ivivu-lang-co-beach.jpg\" style=\"height:400px; width:710px\" /></p>\r\n\r\n<p><em>L&agrave;ng Ch&agrave;i Lăng C&ocirc;.</em></p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng với đặc sản xứ Huế (B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i,...). Qu&yacute; kh&aacute;ch xu&ocirc;́ng thuy&ecirc;̀n R&ocirc;̀ng đ&ecirc;̉ thưởng thức&nbsp;<strong>Ca Hu&ecirc;́ tr&ecirc;n s&ocirc;ng Hương</strong>&nbsp;&ndash; nét văn hóa đ&ocirc;̣c đáo của xứ Hu&ecirc;́</p>\r\n\r\n<h3>NG&Agrave;Y 3: HUẾ - TH&Aacute;NH ĐỊA LA VANG &ndash; ĐỘNG PHONG NHA (ĂN S&Aacute;NG, TRƯA, TỐI) &nbsp; &nbsp;&nbsp;</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m sớm. Rời Huế đi&nbsp;<strong>Phong Nha - Kẻ B&agrave;ng</strong>. Tr&ecirc;n đường, viếng thăm&nbsp;<strong>Th&aacute;nh Địa La Vang</strong>&nbsp;(Được phong tặng l&agrave; Tiểu Vương Cung Th&aacute;nh Đường) v&agrave; ngang qua&nbsp;<strong>Vĩ tuyến 17 S&ocirc;ng Bến Hải - Cầu Hiền Lương</strong>&nbsp;huy&ecirc;̀n thoại.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/25/16/ivivu-tour-da-nang-3n2d-ba-na-hue-dong-phong-nha-thanh-dia-la-vang.jpg\" /></p>\r\n\r\n<p><em>Th&aacute;nh Địa La Vang.</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng tại Phong Nha.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Ngồi thuyền ngược s&ocirc;ng Son chinh phục Động Phong Nha: C&ocirc; Ti&ecirc;n &amp; Cung Đ&igrave;nh dưới s&acirc;u l&ograve;ng n&uacute;i nơi c&oacute; con s&ocirc;ng ngầm từ L&agrave;o chảy sang, chi&ecirc;m ngưỡng c&aacute;c khối thạch nhũ tuyệt đẹp được kiến tạo bởi thi&ecirc;n nhi&ecirc;n qua h&agrave;ng ng&agrave;n thi&ecirc;n ni&ecirc;n kỷ. Khởi h&agrave;nh về Huế theo đường Trường Sơn - Hồ Ch&iacute; Minh.</p>\r\n\r\n<p><em>Hoặc:</em>&nbsp;lựa chọn ngồi xe điện tham quan chinh phục Động Thi&ecirc;n Đường - Ho&agrave;ng cung l&ograve;ng đất sở hữu kỳ quan tr&aacute;ng lệ v&agrave; huyền ảo bậc nhất thế giới. Phụ thu th&ecirc;m 100.000 vnđ/kh&aacute;ch ch&ecirc;nh lệch v&eacute; tham quan).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/08/25/17/ivivu-tour-da-nang-4n3d-hoi-an-ba-na-hue-dong-phong-nha-dong-phong-nha.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Động Thi&ecirc;n Đường.</em></p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng, tự do kh&aacute;m ph&aacute; Cố Đ&ocirc; về đ&ecirc;m với&nbsp;<strong>cầu Tr&agrave;ng Tiền</strong>&nbsp;rực rỡ soi m&igrave;nh xuống d&ograve;ng&nbsp;<strong>s&ocirc;ng Hương</strong>&nbsp;thơ mộng, dạo chơi chợ đ&ecirc;m t&igrave;m hiểu n&eacute;t sinh hoạt của đất kinh th&agrave;nh. Nghỉ kh&aacute;ch sạn 4*.</p>\r\n\r\n<h3>NG&Agrave;Y 4:&nbsp;LĂNG KHẢI ĐỊNH - ĐẠI NỘI &ndash; CH&Ugrave;A THI&Ecirc;N MỤ - TIỄN KH&Aacute;CH (ĂN S&Aacute;NG, TRƯA)&nbsp; &nbsp;</h3>\r\n\r\n<p><strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m.Khởi h&agrave;nh tham quan&nbsp;<strong>Đại Nội - Ho&agrave;ng cung triều Nguyễn</strong>, tham quan Ngọ M&ocirc;n, Điện Th&aacute;i H&ograve;a, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đỉnh,...tiếp tục viếng&nbsp;<strong>Ch&ugrave;a Thi&ecirc;n Mụ</strong>&nbsp;cổ k&iacute;nh l&agrave; nơi lưu giữ nhiều cổ vật qu&yacute; c&oacute; gi&aacute; trị về mặt lịch sử v&agrave; nghệ thuật.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/09/22/14/tour-da-nang-4n3d-hoi-an-ba-na-hue-dong-phong-nha-hue.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p><em>Đại Nội Huế.</em></p>\r\n\r\n<p>Đo&agrave;n tiếp tục di chuyển đến tham quan Lăng Khải Định &ndash; c&ocirc;ng tr&igrave;nh lăng c&oacute; được sự kết hợp tuyệt vời của kiến tr&uacute;c Đ&ocirc;ng - T&acirc;y.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng tại Huế.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Tiễn qu&yacute; kh&aacute;ch ra s&acirc;n bay Huế (c&aacute;c chuyến sau 17:00).V&agrave; s&acirc;n bay Đ&agrave; Nẵng (c&aacute;c chuyến sau 19:00). Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh. Hẹn gặp lại qu&yacute; kh&aacute;ch!</p>', '<p>-&nbsp;Tr&igrave;nh tự chương tr&igrave;nh c&oacute; thể thay đổi nhưng số lượng chương tr&igrave;nh vẫn kh&ocirc;ng thay đổi.</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch c&oacute; mặt tại điểm đ&oacute;n trước 15 ph&uacute;t. Du kh&aacute;ch đến trễ khi xe đ&atilde; khởi h&agrave;nh hoặc hủy tour kh&ocirc;ng b&aacute;o trước vui l&ograve;ng chịu ph&iacute; như &lsquo;hủy v&eacute; ngay ng&agrave;y khởi h&agrave;nh&rsquo;</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch mang theo giấy CMND hoặc Hộ chiếu (Bản ch&iacute;nh), n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ, kh&ocirc;ng mang valy lớn.</p>\r\n\r\n<p>-&nbsp;Trẻ em phải đi c&ugrave;ng cha mẹ hoặc c&oacute; giấy cam kết đi tour c&ugrave;ng người th&acirc;n, khi đi phải mang theo giấy khai sinh hoặc hộ chiếu.</p>\r\n\r\n<p>-&nbsp;Qu&yacute; kh&aacute;ch tự quản l&yacute; tiền bạc tư trang trong qua tr&igrave;nh tham quan du lịch.</p>\r\n\r\n<p>-&nbsp;Khi đăng k&yacute;, qu&yacute; kh&aacute;ch vui l&ograve;ng cung cấp đầy đủ Họ t&ecirc;n v&agrave; năm sinh.</p>\r\n\r\n<p>-&nbsp;Đối với du kh&aacute;ch l&agrave; Việt kiều, Quốc tế nhập cảnh Việt Nam bằng visa rời, vui l&ograve;ng mang theo tờ khai hải quan v&agrave; visa khi đi tour.</p>\r\n\r\n<p>-&nbsp;Chương tr&igrave;nh tour được coi l&agrave; bi&ecirc;n bản ghi nhớ giữa kh&aacute;ch h&agrave;ng v&agrave; Ivivu. Những thắc mắc, khiếu nại sẽ dựa v&agrave;o những quy định trong chương tr&igrave;nh v&agrave; phiếu thu để giải quyết.</p>', 4, '18:03:00', '2020-11-22', '2020-11-25', 'Ô tô', '2020-Nov-Sat_1605945914283.jpg', 14, 0, 0, '2', '2', '2020-11-21 01:05:14', '2020-11-21 01:05:14'),
(11, 'JBnxcnPmiL', 15, 'Tour Buôn Mê Thuột 3N3D: Gia Lai - Kon Tum - Măng Đen', 'Đà Nẵng', 'Măng Đen', 1, 1888000, 1000000, 500000, '<h3>H&ugrave;ng vĩ v&ugrave;ng đất T&acirc;y Nguy&ecirc;n</h3>\r\n\r\n<p>Được xem l&agrave; trung t&acirc;m của văn h&oacute;a T&acirc;y Nguy&ecirc;n, Bu&ocirc;n Ma Thuột quyến rũ du kh&aacute;ch nhờ những c&aacute;nh đồng c&agrave; ph&ecirc; bạt ng&agrave;n, những con th&aacute;c cao ngất h&ugrave;ng vĩ hay vẻ đẹp của d&ograve;ng S&ecirc;r&ecirc;p&ocirc;k huyền thoại. H&atilde;y c&ugrave;ng iVIVU kh&aacute;m ph&aacute; Bu&ocirc;n Ma Thuột nếu bạn đang c&oacute; &yacute; định du lịch đến đ&acirc;y nh&eacute;!</p>\r\n\r\n<p><em><strong>Đặt đo&agrave;n ri&ecirc;ng li&ecirc;n hệ 028 3933 8002 hoặc hotline 0906 355 542 để c&oacute; gi&aacute; tốt 😍</strong></em></p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Bu&ocirc;n M&ecirc; Thuột nổi bật l&ecirc;n giữa T&acirc;y Nguy&ecirc;n bởi...</p>\r\n\r\n<p>-&nbsp;<strong>Nh&agrave; Thờ Gỗ Kon Tum:&nbsp;</strong>thuộc top 10 nh&agrave; thờ đẹp của Việt Nam, một kiệt t&aacute;c bởi được thiết kế theo kiến tr&uacute;c Romantic, phối hợp h&agrave;i ho&agrave; kiểu nh&agrave; s&agrave;n của người Ba Na n&ecirc;n đậm sắc th&aacute;i văn h&oacute;a, t&iacute;n ngưỡng người d&acirc;n cao nguy&ecirc;n từ những đường n&eacute;t họa tiết đến những điểm nhấn tr&ecirc;n chất liệu.</p>\r\n\r\n<p>-&nbsp;<strong>Khu Du lịch Măng Đen:&nbsp;</strong>Rừng nguy&ecirc;n sinh phủ k&iacute;n khắp nơi, kh&iacute; hậu m&aacute;t mẻ quanh năm với nhiều thắng cảnh hồ, th&aacute;c khiến du kh&aacute;ch ngỡ lạc bước giữa &#39;thi&ecirc;n đường nghỉ dưỡng&#39; Măng Đen.</p>\r\n\r\n<p>-&nbsp;<strong>Thủy điện Yaly:&nbsp;&nbsp;</strong>th&aacute;c Yaly được biết đến l&agrave; một th&aacute;c nước đẹp th&igrave; c&ocirc;ng tr&igrave;nh thuỷ điện Yaly đồ sộ bao gồm đập d&acirc;ng, đập tr&agrave;n xả lũ b&ecirc;n một hồ nước trong b&aacute;t ng&aacute;t với mặt hồ rộng hơn 65km2 giữa khung cảnh thi&ecirc;n nhi&ecirc;n đẹp đến nao l&ograve;ng.</p>\r\n\r\n<p>-&nbsp;<strong>Th&aacute;c Dray Nur:&nbsp;</strong>l&agrave; ngọn th&aacute;c tuyệt đẹp thi&ecirc;n nhi&ecirc;n ban tặng cho Đăk Lăk. Dray Nur nghĩa l&agrave; th&aacute;c c&aacute;i, th&aacute;c vợ, gắn liền với mối t&igrave;nh &#39;Romeo v&agrave; Juliet&#39; của n&uacute;i rừng.</p>', '<h3>ĐIỂM Đ&Oacute;N:</h3>\r\n\r\n<p>17h30: Đ&oacute;n kh&aacute;ch tại&nbsp;<strong>367 T&acirc;n Sơn, Phường 15, Quận T&acirc;n B&igrave;nh.</strong></p>\r\n\r\n<p>18h00: Đ&oacute;n kh&aacute;ch tại&nbsp;<strong>Trường Đại Học Hồng B&agrave;ng</strong>, 03 Ho&agrave;ng Việt, Phường 4, Quận T&acirc;n B&igrave;nh.</p>\r\n\r\n<p>18h30: Đ&oacute;n kh&aacute;ch tại&nbsp;<strong>Nh&agrave; Văn H&oacute;a Thanh Ni&ecirc;n</strong>, 04 Phạm Ngọc Thạch, Phường Bến Ngh&eacute;, Quận 1.</p>\r\n\r\n<p>18h45: Đ&oacute;n kh&aacute;ch tại&nbsp;<strong>C&acirc;y xăng Comeco, Ng&atilde; 4 H&agrave;nh Xanh, Phường 21, Quận B&igrave;nh Thạnh</strong>.</p>\r\n\r\n<p>19h30: Đo&aacute;n kh&aacute;ch tại Ng&atilde; 3 Sở Sao,&nbsp;<strong>Th&agrave;nh phố Thủ Dầu Một, B&igrave;nh Dương</strong>.</p>\r\n\r\n<h3>Đ&Ecirc;M 01: TP HỒ CH&Iacute; MINH - MĂNG ĐEN&nbsp;</h3>\r\n\r\n<p>17h00: Xe v&agrave; hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n rời TP Hồ Ch&iacute; Minh theo QL14 đi&nbsp;<strong>Măng Đen</strong>&nbsp;qua S&oacute;c Bombo, B&ugrave; Đăng, đường m&ograve;n Hồ Ch&iacute; Minh, qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tr&ecirc;n xe.</p>\r\n\r\n<h3>NG&Agrave;Y 2: KH&Aacute;M PH&Aacute; MĂNG ĐEN ( ĂN S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>07h30: Đo&agrave;n d&ugrave;ng điểm t&acirc;m tại&nbsp;<strong>Gia Lai</strong>, tiếp tục h&agrave;nh tr&igrave;nh đến với&nbsp;<strong>Măng Đen</strong>.</p>\r\n\r\n<p>09h30: Đo&agrave;n tham quan&nbsp;<strong>Nh&agrave; Thờ Gỗ Kom Tum</strong>&nbsp;tr&ecirc;n 100 tuổi - l&agrave; biểu tượng của v&ugrave;ng đất T&acirc;y Nguy&ecirc;n, l&agrave; niềm tự h&agrave;o của người d&acirc;n th&agrave;nh phố Kon Tum v&agrave; lu&ocirc;n c&oacute; sức h&uacute;t m&atilde;nh liệt cho những du kh&aacute;ch đến với v&ugrave;ng cao nguy&ecirc;n bạt ng&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/03/22/17/ivivu-nha-tho-go-kom-tum.jpg\" /></p>\r\n\r\n<p><em>Nh&agrave; Thờ Gỗ Kon Tum.</em></p>\r\n\r\n<p>11h30: Đo&agrave;n đến với&nbsp;<strong>Măng Đen</strong>&nbsp;- nơi được mệnh danh l&agrave; &ldquo;Đ&agrave; Lạt thứ 2&rdquo; của Việt Nam. Điểm đến n&agrave;y rất đ&aacute;ng để đi bởi kh&ocirc;ng kh&iacute; rất dễ chịu m&aacute;t mẻ quanh năm, đi&ecirc;m t&ocirc; v&agrave;o đ&oacute; l&agrave; n&eacute;t đẹp c&ograve;n nguy&ecirc;n sơ của cao nguy&ecirc;n đại ng&agrave;n, của rừng th&ocirc;ng đầy quyến rũ v&agrave; nơi xứ sở của hồ của th&aacute;c, Măng Đen c&oacute; đến 7 hồ v&agrave; 3 th&aacute;c đẹp tuyệt vời.Ngo&agrave;i ra đ&acirc;y c&ograve;n l&agrave; nơi vẫn c&ograve;n lưu giữ n&eacute;t văn h&oacute;a bản địa mộc mạc khiến cho du kh&aacute;ch kh&ocirc;ng khỏi bở ngỡ v&agrave; t&ograve; m&ograve;. D&ugrave;ng cơm trưa v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/07/09/ivivu-mang-den1.jpg\" /></p>\r\n\r\n<p><em>Măng Đen.</em></p>\r\n\r\n<p>14h00: Xe đưa qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>Khu du lịch th&aacute;c Pa Sĩ,&nbsp;Cầu treo d&acirc;y văng&nbsp;lệch nhịp duy nhất Miền Trung - T&acirc;y Nguy&ecirc;n</strong>; tham quan&nbsp;<strong>th&aacute;c Đăk Ke&nbsp;v&agrave; hồ Đăk Ke</strong>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/11/29/11/ivivu-khu-du-lich-thac-pa-si.jpg\" /></p>\r\n\r\n<p><em>Th&aacute;c Pa Sĩ.</em></p>\r\n\r\n<p>15h30:&nbsp;Qu&yacute; kh&aacute;ch tham quan ch&ugrave;a&nbsp;<strong>Kh&aacute;nh L&acirc;m</strong>&nbsp;- nơi thiền định giữa rừng thi&ecirc;ng, sau đ&oacute; tham quan&nbsp;<strong>Đức mẹ Măng Đen</strong>.&nbsp;</p>\r\n\r\n<p>18h00: Đo&agrave;n d&ugrave;ng cơm tối tại nh&agrave; h&agrave;ng với c&aacute;c m&oacute;n đặc sản n&uacute;i rừng, nghỉ đ&ecirc;m tại<strong>&nbsp;Măng Đen</strong>.&nbsp;</p>\r\n\r\n<h3>NG&Agrave;Y 3: MĂNG ĐEN- GIA LAI- BU&Ocirc;N M&Ecirc; THUỘT ( ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>06h00: Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m tại nh&agrave; h&agrave;ng, sau đ&oacute; l&agrave;m thủ tục trả ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>09h30: Đến với Gia Lai, đo&agrave;n tham quan&nbsp;<strong>thủy điện Yaly, Biển Hồ</strong>&nbsp;- một địa danh nổi tiếng của Pleiku &nbsp;nơi&nbsp; m&agrave; được nhạc sĩ Nguyễn Cường đưa v&agrave;o b&agrave;i h&aacute;t rất nổi tiếng &ldquo; Đ&ocirc;i mắt Pleiku Biển Hồ Đầy&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/11/29/11/ivivu-thuy-dien-yaly-bien-ho.jpg\" /></p>\r\n\r\n<p><em>Thủy Điện Yaly.</em></p>\r\n\r\n<p>11h30: Đo&agrave;n d&ugrave;ng buổi trưa tai nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>12h30: Tiếp tục khởi h&agrave;nh về&nbsp;<strong>Bu&ocirc;n M&ecirc; Thuột.</strong></p>\r\n\r\n<p>16h00: Đo&agrave;n tham quan&nbsp;<strong>bu&ocirc;n l&agrave;ng Ako Dh&ocirc;ng</strong>&nbsp;của d&acirc;n tộc &Ecirc;đ&ecirc; trong l&ograve;ng Th&agrave;nh Phố.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/03/22/17/ivivu-buon-lang-ako-dhong.jpg\" /></p>\r\n\r\n<p><em>Bu&ocirc;n L&agrave;ng&nbsp;Ako Dh&ocirc;ng.</em></p>\r\n\r\n<p>Về nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>19h00: Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; phố N&uacute;i về đ&ecirc;m, tự t&uacute;c kh&aacute;m ph&aacute; &acirc;m thực xứ n&uacute;i, dừng ch&acirc;n thưởng thức &ldquo;ly caf&eacute; Bu&ocirc;n M&ecirc;&rdquo; hoặc thưởng thức đặc sản n&uacute;i rừng c&ugrave;ng nh&acirc;m nhi rượu cần nổi tiếng.</p>\r\n\r\n<h3>NG&Agrave;Y 4: BU&Ocirc;N M&Ecirc; THUỘT- HỒ CH&Iacute; MINH ( S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>06h00: Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m s&aacute;ng, sau đ&oacute; l&agrave;m thủ tục trả ph&ograve;ng kh&aacute;ch sạn. Tham quan&nbsp;<strong>ch&ugrave;a Khải Đoan</strong>, ng&ocirc;i ch&ugrave;a lớn v&agrave; đep nhất xứ Ban M&ecirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/06/21/17/ivivu-chua-khai-doan.jpg\" /></p>\r\n\r\n<p><em>Ch&ugrave;a Khải Đoan.</em></p>\r\n\r\n<p>09h00: Xe đưa đo&agrave;n trở về Hồ Ch&iacute; Minh, qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>th&aacute;c Dray Nur</strong>, ngọn th&aacute;c h&ugrave;ng vỹ nổi tiếng của T&acirc;y Nguy&ecirc;n. D&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/03/22/17/ivivu-thac-dray-nur.jpg\" /></p>\r\n\r\n<p><em>Th&aacute;c Dray Nur.</em></p>', '<p>-&nbsp;<strong>&nbsp;Gi&aacute; tr&ecirc;n kh&ocirc;ng &aacute;p dụng cho ng&agrave;y lễ, tết.</strong></p>\r\n\r\n<p>- Tr&igrave;nh tự chương tr&igrave;nh c&oacute; thể thay đổi nhưng số lượng chương tr&igrave;nh vẫn kh&ocirc;ng thay đổi.</p>\r\n\r\n<p>-&nbsp;Du kh&aacute;ch c&oacute; mặt tại điểm đ&oacute;n trước 15 ph&uacute;t. Du kh&aacute;ch đến trễ khi xe đ&atilde; khởi h&agrave;nh hoặc hủy tour kh&ocirc;ng b&aacute;o trước vui l&ograve;ng chịu ph&iacute; như &lsquo;hủy v&eacute; ngay ng&agrave;y khởi h&agrave;nh&rsquo;</p>\r\n\r\n<p>- Du kh&aacute;ch mang theo giấy CMND hoặc Hộ chiếu (Bản ch&iacute;nh), n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ, kh&ocirc;ng mang valy lớn.</p>\r\n\r\n<p>- Trẻ em phải đi c&ugrave;ng cha mẹ hoặc c&oacute; giấy cam kết đi tour c&ugrave;ng người th&acirc;n, khi đi phải mang theo giấy khai sinh hoặc hộ chiếu.</p>\r\n\r\n<p>-&nbsp;Du kh&aacute;ch tự quản l&yacute; tiền bạc tư trang trong qua tr&igrave;nh tham quan du lịch.</p>\r\n\r\n<p>-&nbsp;Khi đăng k&yacute;, qu&yacute; kh&aacute;ch vui l&ograve;ng cung cấp đầy đủ Họ t&ecirc;n v&agrave; năm sinh.</p>\r\n\r\n<p>-&nbsp;Đối với du kh&aacute;ch l&agrave; Việt kiều, Quốc tế nhập cảnh Việt Nam bằng visa rời, vui l&ograve;ng mang theo tờ khai hải quan v&agrave; visa khi đi tour.</p>\r\n\r\n<p>Chương tr&igrave;nh tour được coi l&agrave; bi&ecirc;n bản ghi nhớ giữa kh&aacute;ch h&agrave;ng v&agrave; C&ocirc;ng Ty. Những thắc mắc, khiếu nại sẽ dựa v&agrave;o những quy định trong chương tr&igrave;nh v&agrave; phiếu thu để giải quyết.</p>', 3, '19:07:00', '2020-11-22', '2020-11-23', 'Ô tô', '2020-Nov-Sat_1605946113286.jpg', 0, 0, 0, '2', '2', '2020-11-21 01:08:33', '2020-11-21 01:08:33'),
(12, 'ncStmRu59S', 15, 'Tour Bình Hưng 2N2D: Lặn Ngắm San Hô - BBQ - Vườn Nho', 'Đà Nẵng', 'Nha trang Lặn Ngắm San Hô - BBQ - Vườn Nho', 12, 1490000, 1090000, 1000000, '<h3>Chạm tay đến &ldquo;Maldives Việt Nam&rdquo;</h3>\r\n\r\n<p>B&igrave;nh Hưng l&agrave; một mảnh gh&eacute;p kh&ocirc;ng thể thiếu trong bức tranh tứ b&igrave;nh m&agrave; thi&ecirc;n nhi&ecirc;n đặc biệt ưu đ&atilde;i cho Nha Trang. Nằm ẩn dưới ch&acirc;n đ&egrave;o tr&ecirc;n cung đường biển Vĩnh Hy &ndash; B&igrave;nh Ti&ecirc;n, h&ograve;n đảo xinh đẹp hiện ra với hang đ&aacute; nước ngọt rất đặc biệt, l&agrave;n nước trong xanh nh&igrave;n r&otilde; những dải san h&ocirc; tuyệt đẹp b&ecirc;n dưới, b&atilde;i biển thoai thoải với nhiều tảng đ&aacute; h&igrave;nh th&ugrave;, m&agrave;u sắc sinh động, kiểu d&aacute;ng phong ph&uacute;, c&oacute; những tảng đ&aacute; c&ograve;n phủ k&iacute;n r&ecirc;u xanh. Du kh&aacute;ch sẽ được thưởng thức những m&oacute;n hải sản tươi ngon nhất vừa được đ&aacute;nh bắt từ biển về, ngo&agrave;i ra c&ograve;n m&oacute;n t&ocirc;m h&ugrave;m trứ danh bởi nơi n&agrave;y c&ograve;n nổi tiếng với nghề nu&ocirc;i t&ocirc;m h&ugrave;m. C&ugrave;ng iVIVU kh&aacute;m ph&aacute; ngay h&ocirc;m nay!</p>\r\n\r\n<p><br />\r\n<em><strong>Đặt đo&agrave;n ri&ecirc;ng li&ecirc;n hệ 028 3933 8002 hoặc hotline 0906 355 542 để c&oacute; gi&aacute; tốt 😍</strong></em></p>\r\n\r\n<h3>Những trải nghiệm th&uacute; vị trong chương tr&igrave;nh</h3>\r\n\r\n<p>Nổi bật l&ecirc;n giữa cụm đảo tứ b&igrave;nh, B&igrave;nh Hưng thu h&uacute;t bởi...</p>\r\n\r\n<p>-&nbsp;<strong>B&atilde;i Kinh:</strong>&nbsp;l&agrave; nơi tập trung c&aacute;c kh&aacute;ch du lịch chuẩn bị xuất ph&aacute;t đi đảo B&igrave;nh Hưng. Nơi đ&acirc;y c&oacute; b&atilde;i tắm c&aacute;t trắng trải d&agrave;i với l&agrave;n nước biển trong xanh v&agrave; những h&ograve;n đ&aacute; với đầy đủ k&iacute;ch cỡ, h&igrave;nh th&ugrave; độc đ&aacute;o chạy dọc ra biển như muốn &ocirc;m trọn v&agrave; g&igrave;n giữ vẻ đẹp của b&atilde;i.</p>\r\n\r\n<p>-&nbsp;<strong>Hải đăng H&ograve;n Ch&uacute;t:</strong>&nbsp;ngọn hải đăng c&oacute; tuổi đời l&acirc;u nhất l&ecirc;n đến hơn 100 năm tuổi, được coi l&agrave; biểu tượng của B&igrave;nh Hưng hoang sơ, đứng tr&ecirc;n đỉnh ngọn m&agrave; nh&igrave;n ra to&agrave;n cảnh biển th&igrave; phải&nbsp;đẹp đến ngẩn ngơ.</p>', '<h3>TỐI NG&Agrave;Y 1: TP.HCM -&nbsp; ĐẢO B&Igrave;NH HƯNG</h3>\r\n\r\n<p>20h00: Xe v&agrave; HDV đ&oacute;n kh&aacute;ch tại điểm hẹn, Đo&agrave;n khởi h&agrave;nh đi Cam Ranh, tr&ecirc;n đường đi được giao lưu, h&aacute;t với nhau c&ugrave;ng HDV vui nhộn. Những clip h&agrave;i, nhạc nhẹ sẽ gi&uacute;p qu&yacute; kh&aacute;ch x&oacute;a tan sự căng thẳng trong c&ocirc;ng việc hằng ng&agrave;y. Nghỉ đ&ecirc;m tr&ecirc;n xe.</p>\r\n\r\n<p><strong>- Địa điểm 1: Nh&agrave; thiếu nhi quận 10. ( 139 bắc hải)</strong></p>\r\n\r\n<p><strong>- Địa điểm 2: C&acirc;y xăng comeco số 3 gần ng&atilde; tư H&agrave;ng Xanh.</strong></p>\r\n\r\n<p><strong>- Địa điểm 3: Ng&atilde; Tư Thủ Đức, Suối ti&ecirc;n, Amata.</strong></p>\r\n\r\n<h3>NG&Agrave;Y 1: B&Igrave;NH HƯNG &ndash; HẢI ĐĂNG H&Ograve;N CH&Uacute;T &ndash; NGẮM SAN H&Ocirc; ( ĂN&nbsp;S&Aacute;NG, TRƯA, TỐI)</h3>\r\n\r\n<p>05h00: Đo&agrave;n dừng ch&acirc;n tại trạm dừng ch&acirc;n vệ sinh c&aacute; nh&acirc;n.</p>\r\n\r\n<p>05h30: Qu&yacute; kh&aacute;ch tiếp tục h&agrave;nh tr&igrave;nh đến với&nbsp;<strong>B&atilde;i Kinh</strong>, xe đưa qu&yacute; kh&aacute;ch qua cung đường ven biển mới tuyệt đẹp. Tr&ecirc;n đường đi qu&yacute; kh&aacute;ch như được chi&ecirc;m ngưỡng một bức tranh thi&ecirc;n nhi&ecirc;n ban tặng với một b&ecirc;n l&agrave; n&uacute;i non h&ugrave;ng vĩ một b&ecirc;n biển xanh trong vắt. Đến B&atilde;i Kinh Qu&yacute; kh&aacute;ch Tự do tắm biển -&nbsp;Một b&atilde;i biển tuyệt đẹp, l&agrave; kết quả tuyệt hảo của tạo h&oacute;a d&agrave;nh tặng ri&ecirc;ng cho v&ugrave;ng đất kh&ocirc; cằn n&agrave;y, vị tr&iacute; cũng như khung cảnh của một b&atilde;i biển nhỏ xinh đ&oacute; chỉ mới nh&igrave;n một lần chắc chắn bạn sẽ thấy m&ecirc; ngay lập tức.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/09/05/16/ivivu-binhhung-binhlap-binhtien2n2d-bai-kinh.jpg\" /></p>\r\n\r\n<p><em>B&atilde;i Kinh.</em></p>\r\n\r\n<p>07h00: L&ecirc;n t&agrave;u qua&nbsp;<strong>đảo B&igrave;nh Hưng</strong>&nbsp;(10 ph&uacute;t). &ndash; Chi&ecirc;m ngưỡng vẻ đẹp thi&ecirc;n nhi&ecirc;n hoang sơ hùng vĩ của Vịnh Cam Ranh &ndash; L&agrave; một trong những vịnh biển đẹp nhất miền Trung với vùng biển trong xanh m&agrave;u ngọc bích.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2016/09/13/11/ivivu-tour-binh-hung-2n3d-ruou-tiec-noi-bar-tren-bien-bbq.jpg\" /></p>\r\n\r\n<p><em>B&igrave;nh Hưng.</em></p>\r\n\r\n<p>Ăn sáng tr&ecirc;n đảo với thực đơn t&ocirc; + ly.</p>\r\n\r\n<p>08h30: Qu&yacute; kh&aacute;ch l&ecirc;n xe điện tham quan&nbsp;<strong>B&atilde;i đ&aacute; trứng</strong>&nbsp;tự nhi&ecirc;n c&oacute; lịch sử h&agrave;ng hơn cả trăm năm. Tiếp đến qu&yacute; kh&aacute;ch qua&nbsp;<strong>KDL An Hill</strong>&nbsp;chi&ecirc;m ngắm&nbsp;<strong>C&acirc;y Cầu Bảy Sắc Cầu Vồng Ven Biển</strong>&nbsp;đang l&agrave; điểm đến th&uacute; vị, mới lạ nhiều g&oacute;c chụp ảnh tuyệt đẹp hấp dẫn tất cả mọi du kh&aacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/25/16/ivivu-binh-hung.jpg\" /></p>\r\n\r\n<p><em>Cầu Bảy Sắc Cầu Vồng Ven Biển.</em></p>\r\n\r\n<p>Ngắm ngọn&nbsp;<strong>Hải Đăng H&ograve;n Ch&uacute;t</strong>&nbsp;từ xa, đ&acirc;y l&agrave; ngọn hải đăng c&oacute; tuổi thọ hơn 100 tuổi, từ l&acirc;u đ&atilde; l&agrave; nguồn s&aacute;ng cho c&aacute;c ngư d&acirc;n đi t&agrave;u cập bến. Bạn cũng c&oacute; thể ngắm trọn vẹn những cung đường biển đẹp tuyệt vời.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/12/08/16/ivivu-hai-dang-hon-chut.jpg\" /></p>\r\n\r\n<p><em>Hải Đăng H&ograve;n Ch&uacute;t.</em></p>\r\n\r\n<p>09h30: Qu&yacute; kh&aacute;ch nhận ph&ograve;ng. Tự do nghỉ ngơi, tắm biển hay kh&aacute;m ph&aacute; đảo.</p>\r\n\r\n<p>11h30: Tham quan b&egrave; nổi du lịch, nơi nu&ocirc;i các loại hải sản như: T&ocirc;m h&ugrave;m, c&aacute; b&oacute;p, c&aacute; chim, c&aacute; m&uacute;, ốc. Tự do sáng tác ảnh tại bè nổi với khu vực background được bố trí, x&acirc;y dựng các c&ocirc;ng trình &ldquo;sống ảo&rdquo; v&ocirc; cùng c&ocirc;ng phu, độc đáo như: N&acirc;́c thang l&ecirc;n thi&ecirc;n đường, xích đu tr&ecirc;n bi&ecirc;̉n, chong ch&oacute;ng t&igrave;nh y&ecirc;u.</p>\r\n\r\n<p>D&ugrave;ng bữa trưa đặc sản tại nh&agrave; b&egrave; nổi với những m&oacute;n ăn d&acirc;n d&atilde; mang đậm chất miền biển.</p>\r\n\r\n<p>14h00: Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u tham quan một v&ograve;ng quanh đảo với những cảnh đẹp qu&yacute; kh&aacute;ch phải thốt n&ecirc;n lời:&nbsp;<strong>B&atilde;i Đ&aacute; Trứng, B&atilde;i Chướng, B&atilde;i Ch&agrave; L&agrave;. T</strong>ại đ&acirc;y qu&yacute; kh&aacute;ch tham gia chương giao lưu văn nghệ &ldquo;chương tr&igrave;nh BAR tr&ecirc;n biển&rdquo; vừa tắm biển, vừa thưởng thức rượu vang c&ugrave;ng nhăm nhi tr&aacute;i c&acirc;y,&nbsp; lặn ngắm những Rạng san h&ocirc; đủ m&agrave;u sắc tr&ecirc;n những phao nổi tr&ecirc;n biển, cảm gi&aacute;c rất thoải m&aacute;i v&agrave; th&uacute; vị. Đặc biệt b&atilde;i tắm suối Nước Ngọt; nơi d&ograve;ng nước suối trong m&aacute;t, từ tr&ecirc;n đỉnh n&uacute;i Ch&uacute;a đổ thẳng xuống biển (qu&yacute; kh&aacute;ch sẽ cảnh gi&aacute;c vừa tắm biển vừa tắm suối c&ugrave;ng một nơi).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2017/11/08/17/ivivu-bai-cua-bai-chuong.jpg\" /></p>\r\n\r\n<p><em>B&atilde;i Chướng.</em></p>\r\n\r\n<p>17h00: T&agrave;u đưa qu&yacute; kh&aacute;ch về lại đảo, Qu&yacute; kh&aacute;ch vệ sinh c&aacute; nh&acirc;n để chuẩn bị cho chương trinh BBQ hải sản đặc sắc buổi tối.</p>\r\n\r\n<p>18h30: Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<strong>B&egrave; Nổi</strong>&nbsp;để tham gia bữa tiệc hải sản T&ocirc;m H&ugrave;m với kh&ocirc;ng kh&iacute; th&acirc;n mật, c&ugrave;ng nhau giao lưu giọng ca v&agrave;ng với nhiều m&oacute;n qu&agrave; lạ v&agrave; hấp dẫn.</p>\r\n\r\n<p>Tối: Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; đảo B&igrave;nh Hưng về đ&ecirc;m với c&aacute;c m&oacute;n d&acirc;n d&atilde; như: b&aacute;nh khọt, b&aacute;nh x&egrave;o, b&aacute;nh tr&aacute;nh trộn, lụi nướng, nem nướng, nước s&acirc;m.</p>\r\n\r\n<h3>NG&Agrave;Y 2: B&Igrave;NH HƯNG - THAM QUAN ĐỒI CỪU - VƯỜN NHO - TP.HCM ( ĂN S&Aacute;NG, TRƯA)</h3>\r\n\r\n<p>S&aacute;ng: Qu&yacute; kh&aacute;ch c&oacute; thể dạy sớm tắm biển, sau đ&oacute; hướng dẫn vi&ecirc;n tập hợp đo&agrave;n ăn s&aacute;ng c&ugrave;ng thưởng thức m&oacute;n ăn thơm ngon miền biển.</p>\r\n\r\n<p>08h00: Qu&yacute; kh&aacute;ch tự do vui chơi kh&aacute;m ph&aacute; đảo, hoặc mua sắm đặc sản b&igrave;nh hưng bao gồm: Rong biển, C&aacute; kh&ocirc;, Mực, B&aacute;nh Khoai, B&aacute;nh Xo&agrave;i.</p>\r\n\r\n<p>08h30: Qu&yacute; kh&aacute;ch trả ph&ograve;ng tạm biệt đảo B&igrave;nh Hưng, l&ecirc;n t&agrave;u về lại đất liền.</p>\r\n\r\n<p>09h00: Xe đưa Qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>Đồi Cừu Suối Ti&ecirc;n</strong>&nbsp;một trong những khu sinh th&aacute;i mới mở c&ograve;n tr&agrave;n đầy n&eacute;t tự nhi&ecirc;n nguy&ecirc;n sơ, bạn sẽ c&oacute; cảm gi&aacute;c như lạc v&agrave;o n&ocirc;ng trại cừu cổ t&iacute;ch ch&acirc;u &acirc;u, kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng, bạt ng&agrave;n với m&agrave;u xanh tươi mới của cỏ c&acirc;y.( tự t&uacute;c).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2019/10/25/16/ivivu-doi-cuu-suoi-tien.jpg\" /></p>\r\n\r\n<p><em>Đồi Cừu Suối Ti&ecirc;n.</em></p>\r\n\r\n<p>10h00: Đo&agrave;n tiếp tục đi tham quan&nbsp;<strong>Vườn Nho</strong>, đến đ&acirc;y, kh&aacute;ch sẽ được thoải m&aacute;i chụp ảnh với những ch&ugrave;m nho trĩu quả. Nếu muốn, kh&aacute;ch c&oacute; thể mua nho trực tiếp tại vườn, hoặc mua c&aacute;c đặc sản l&agrave;m từ nho như: nước &eacute;p, rượu nho.</p>', '<p>-&nbsp;Trong trường hợp kh&aacute;ch h&agrave;ng dẫn theo trẻ em dưới 15 tuổi (kh&ocirc;ng phải con ruột) đi c&ugrave;ng trong chương tr&igrave;nh, phải mang theo Giấy Ủy Quyền của cha mẹ, c&oacute; x&aacute;c nhận của ch&iacute;nh quyền địa phương.</p>\r\n\r\n<p>- Trẻ em phải đi c&ugrave;ng cha mẹ khi đi phải mang theo giấy khai sinh hoặc hộ chiếu.</p>\r\n\r\n<p>- Du kh&aacute;ch c&oacute; mặt tại điểm đ&oacute;n trước 30 ph&uacute;t. Du kh&aacute;ch đến trễ khi xe đ&atilde; khởi h&agrave;nh hoặc hủy tour kh&ocirc;ng b&aacute;o trước vui l&ograve;ng chịu ph&iacute; như &lsquo;hủy v&eacute; ngay ng&agrave;y khởi h&agrave;nh&rsquo;.</p>\r\n\r\n<p>- Du kh&aacute;ch mang theo giấy CMND hoặc Hộ chiếu (Bản ch&iacute;nh), n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ, kh&ocirc;ng mang vali lớn.</p>\r\n\r\n<p>- Du kh&aacute;ch tự quản l&yacute; tiền bạc tư trang trong qua tr&igrave;nh tham quan du lịch.</p>\r\n\r\n<p>- Khi đăng k&yacute;, qu&yacute; kh&aacute;ch vui l&ograve;ng cung cấp đầy đủ Họ t&ecirc;n v&agrave; năm sinh.</p>\r\n\r\n<p>- Đối với du kh&aacute;ch l&agrave; Việt kiều, Quốc tế nhập cảnh Việt Nam bằng visa rời, vui l&ograve;ng mang theo tờ khai hải quan v&agrave; visa khi đi tour.</p>\r\n\r\n<p>- Tour mang t&iacute;nh chất l&agrave; tour gh&eacute;p lẻ,v&igrave; vậy Tour sẽ thực hiện nếu c&oacute; đủ lượng kh&aacute;ch.Trường hợp,kh&ocirc;ng đủ kh&aacute;ch để khởi h&agrave;nh C&ocirc;ng Ty sẽ ho&agrave;n 100% tiền lại cho kh&aacute;ch v&agrave; kh&ocirc;ng đền b&ugrave; chi ph&iacute; thiệt hại ph&aacute;t sinh nếu c&oacute;. Mong qu&yacute; kh&aacute;ch lưu &yacute; !</p>', 2, '18:03:00', '2020-11-25', '2020-11-27', 'Ô tô', '2020-Nov-Sat_1605949511696.jpg', 10, 0, 0, '2', '2', '2020-11-21 02:05:11', '2020-11-21 02:05:11');

